@-moz-document domain("star.longzhu.com") {
.top-banner-pic,
.stage-container-radio,
.stage-container-rank,
.stage-container-widget {
    display: none
}
.stage-container-chatroom {
    margin-top:-216px
}
.stage-container-host-card {
    margin-right:0px!important;
}
.header {
    padding-left: 60px;
    position: relative;
}
.header-avatar.report-rbi-click {
    top: 10px;
    left: 10px;
    width: 50px;
    height: 50px;
}
.medals-wrap {
    margin-top:-30px!important;
}
.header-info-grade{
    margin-left:-70px!important;
}
.header,
.stage-container-host-card,
.stage-container-head{
    height:75px!important;
}
}

@-moz-document domain("star.longzhu.com") {
.topbar-hotsearch-layer, 
.task_finished_dialog,
.small-games, 
.topbar-icon-pk2,
.topbar-download,
.report-rbi-hover-challenge,
.room-medals,
.host-bg,
.live-ending,
