    display: none!important;
}
}

@-moz-document domain("star.longzhu.com") {
#rank-list-cntr, 
.chatroom-ap,
.user_enter_msg, 
.upgrade_tips.pt-chat-plugins-cell,
.horn-list,
.buy-noble-icon, 
.user-guard-icon, 
.snotice-plugin,
.user-vip-lvl,
.pt-cars-enter,
.medal-item, 
.pt-chat-plugins-draw-red-envelope-cell, 
.pt-chat-plugins-gift-cell, 
