css:
@-moz-document url-prefix("https://tour.golang.org/") {
.top-bar { 
	background: #111;
	color: #999
}

.ui-draggable {
 	background: #333 !important;   
}

#editor-container {
background: #4b4b4b;
color: #aaa;
}

#explorer {
    background: #222;
}

.menu-button {
    background: #333;
    color: #808080;
    transition: 0.5s;
}

.menu-button.active {
    background: #111;
    color: #fff
}

#right-side {
	background: #111;
}

#file-editor {
	background: #1E1E1E;
}

.CodeMirror-gutters {
    border-width: 0.25px;   
 	border-color: #6c6c6c;   
}

#file-editor .CodeMirror-lines, #file-editor .CodeMirror-gutters {
    background: #1E1E1E;
    color: #D4D4D4
}

#file-menu {
    background: #222;
}

.slide-content {
	background: #222;
    color: #999;
}

pre {
	background: #333;
    color: #999
}

code, code-mirror, .CodeMirror-scroll {
    background: #505050;
    color: #fff
}

.CodeMirror-lines {
  background: #222;
}

h1, h2, h3, h4 {
 	color: #999   
}

.output > pre {
    background: #111;
}

.output {
    background-color: #111;
    background-image: url(https://i.imgur.com/RDs9Tla.png);
}

.cm-keyword {
color: #569CD6 !important;
}

.cm-atom{
color: #569CD6 !important;
}

.cm-comment{
color: #608B4E !important;
}

.cm-number{
color: #B5CEA8 !important;
}

.cm-string {
color: #CE9178 !important;
}

.CodeMirror-linenumber {
color: #808080;
}

.bar.module-bar {
background-color: #000;
}

.menu-button.active {
background-color: #505050;
}
}
