css:
@-moz-document domain("ellie-app.com") {
.cm-s-material.CodeMirror,
.Pages-Editor-Layout-editorContainer,
.Pages-Editor-Output-container,
.Pages-Editor-Output-details,
.Ellie-Ui-Button-primary,
.Pages-Editor-Header-header,
.Pages-Editor-Sidebar-container,
.Ellie-Ui-Sections-button,
.Ellie-Ui-Package-container,
.Pages-Editor-Sidebar-ad,
.cm-s-material .CodeMirror-gutters,
html,
.Pages-Editor-Sidebar-packagesSectionTitle,
.Pages-Editor-Sidebar-aboutHeading,
.Pages-Editor-Sidebar-aboutParagraph,
.Ellie-Ui-Button-link,
.Ellie-Ui-Toast-container,
.Ellie-Ui-Package-project,
.Ellie-Ui-Package-infoText,
.Ellie-Ui-Setting-title,
.Ellie-Ui-Setting-description,
.Ellie-Ui-Setting-description,
.Pages-Editor-Sidebar-vimModeLabel,
.Pages-Editor-Sidebar-aboutCopyright,
.Pages-Editor-Output-failureMessage,
.Pages-Editor-Output-failureHint,
.Ellie-Ui-TextInput-input
{
    background-color: #fff !important;
    color: #000 !important;
}

.Ellie-Ui-Ad-container,
.Ellie-Ui-Sections-buttonOpen,
.Pages-Editor-Header-header,
.Ellie-Ui-Button-accent
{
 box-shadow: 0 1px 0 0 rgba(0, 0, 0, 0.5) !important;
}

.Pages-Editor-Layout-sidebar,
.Pages-Editor-Layout-outputAndLogsContainer
{
 box-shadow: 0 0 1px 0 rgba(0, 0, 0, 0.5) !important;
}

.Ellie-Ui-Toast-container
{
    border-left: none;
}

.Ellie-Ui-Toast-container,
.Pages-Editor-Output-details,
.Ellie-Ui-Button-primary,
.Ellie-Ui-Package-container,
.Pages-Editor-Output-failureMessage
{
    box-shadow: 0 1px 1px 1px rgba(0, 0, 0, 0.3) !important;
}

.Pages-Editor-Header-logo,
.Ellie-Ui-Icon-icon
{
    fill: hsl(0, 0%, 36%) !important;  
}

.Pages-Editor-Layout-editorContainer:last-child {
    border-top: none !important;
}

.Pages-Editor-Layout-logsContainer {
    border-top-width: 1px !important;
}

.Ellie-Ui-Sections-section:not(:first-child)
{
    border-top: 1px solid rgba(0, 0, 0, 0.3) !important;
}

.CodeMirror-focused .CodeMirror-activeline-background,
.CodeMirror-focused .CodeMirror-activeline-gutter
{
    background-color: transparent !important;
}

.cm-s-material .CodeMirror-cursor {
    border-left: 1px solid #000 !important;
}

.cm-s-material .cm-string,
.cm-s-material .cm-property
{
    color: #00b52a;
}

.cm-s-material .cm-variable {
    color: #0060ff;
}

.CodeMirror .cm-keyword {
    color: #ff00a1 !important;
}

.cm-s-material .cm-variable-3,
.cm-s-material .cm-type
{
    color: hsla(45, 100%, 40%, 1);
}

.cm-s-material .cm-def,
.cm-s-material .cm-operator
{
    color: #000;
}


.Ellie-Ui-Button-link,
.Ellie-Ui-Sections-button,
.Ellie-Ui-Package-project
{
    font-weight: normal !important;
}

.CodeMirror-selected {
    background:#ddd !important;
}

.Ellie-Ui-Button-accent {
 	background-color: #ffbdea;
    color: #000;   
}
}
