css:
@-moz-document domain("komplett.no") {
.navigation-header-main {
    background: #3c3c3c;
    color: #ccc;
}
.navigation-header {
    background: #333 !important;
}
.navigation-menu ul span {
    color: #ccc !important;
}
.navigation-menu li:hover {
    color: #333 !important;
}
.navigation-header-user-menu__list-item-link {
    color: #ccc !important;
}
.navigation-header-user-menu__list-item-link:hover {
    background: #ccc !important;
    color: #333 !important;
}
body {
    color: #ccc !important;
}
#MainContent {
    background: #555;
}
.container {
    background: #333 !important;
}
.main-body {
    background: #333 !important;
}
.maincontent-container {
    background: #333 !important;
    color: #ccc;
}
.content-block .text-content {
    color: #ccc !important;
}
.search-view-controls {
    background: #555 !important;
}
.values {
    color: #333;
}
.recommendations-title {
    color: #333;
}
.ubo-content {
    background: #333;
}
.product-price,
.product-price-before {
    color: #ccc !important;
}
.newsletter {
    background: #555 !important;
}
.action-container span {
    color: #ccc !important;
}
.navigation-menu__container .navigation-menu__group {
    background: #555 !important;
}
.navigation-menu__container .navigation-menu__group span {
    color: #333 !important;
}
.wishlist-container {
    background: #333;
}
.wishlist-container a {
    color: #ccc !important;
}
.wishlist-container div {
    color: #ccc !important;
}
footer {
    background: #333 !important;
}
footer .link-collection a {
    color: #ccc !important;
}
.product-technical-details{
    color: #333;
}
.review-row{
    color: #333;
}
.caas .product-main-info .links .linkbox{
   background: #9e9e9e;
}
element.style{
   background-color: #3c3c3c;
}
.order-details .info-row .responsive-table caption{
   color: #d6d6d6;
   background-color: #333333;
}
.order-details .info-row .responsive-table td, .order-details .info-row .responsive-table th{
   border-color: #333333;
   background-color: #333333;
}
.responsive-table tbody td, .responsive-table tbody th{
   color: #afafaf;
   background-color: #333333;
}
.btn-large, .btn-medium, .dropdown-btn select, .btn-small, .button-list-item{
   border-bottom: solid #9a9a9a;
   background-color: #333333;
}
.tertiary-grey.btn-large, .tertiary-grey.btn-medium, .dropdown-btn select.tertiary-grey, .tertiary-grey.btn-small, .tertiary-grey.button-list-item{
   border-top: 1px solid #8a8a8a;
   border-right: 1px solid #8a8a8a;
   border-left: 1px solid #8a8a8a;
   background: #8a8a8a;
}
.order .products-table .table-header.first-header .cell.info{
	border-bottom: 1px solid #333333;
   border-top: 1px solid #3a3a3a;
   background-color: #3c3c3c;
}
.order .product-list-footer{
   background: #333333
}
body .caas, html .caas {
   background-color: #555555;
}
.caas .navigation-header .main-header{
   background: #3c3c3c;
}
.caas .product-main-info__body{
   background-color: #3c3c3c;
}
.caas .product-main-info .product-main-info-webtext2{
   background-color: #3c3c3c;
}
.caas .product-page-boxes .responsive-content-wrapper .product-page .product-main-info.row{
   background-color: #3c3c3c;
}
.caas .product-main-info .product-main-info-buy{
   background-color: #3c3c3c;
}
.caas .product-section .product-section-content .review-rating-average{
   background-color: #3c3c3c;
}
.caas .product-section .product-section-header{
   background-color: #3c3c3c;
}
.caas p{
   background-color: #3c3c3c;
}
.caas .row{
   background-color: #3c3c3c;
   color: #d4d0d0;
}
.caas .new-position .reviews{
   background: #3c3c3c;
}
.caas .product-section{
   border-top: 1px solid #3c3c3c;
   background-color: #3c3c3c;
}
.caas .breadcrumbs{
   border-bottom: 1px solid #3c3c3c;
   background-color: #3c3c3c;
}
.caas .product-page-boxes .breadcrumbs-wrapper{
   background: #3c3c3c;
}
.caas .product-main-info .product-main-info-webtext1{
   color: #c3c3c3
}
.caas .product-section:last-child:not(:first-child){
   border-bottom: #3c3c3c;
}
.caas .product-section.technical-details{
   background-color: #3c3c3c;
}
.caas .product-section.technical-details.isopen{
   background-color: #3c3c3c;
}
.caas .product-page-boxes .responsive-content-wrapper .product-page .product-secondary-info-columns .product-recommendations-right .recommendations .swiper-previous-body-next-container{
   background-color: #3c3c3c;
}
.caas .product-page-boxes .responsive-content-wrapper .product-page .product-secondary-info-columns .product-recommendations-right .recommendations-title{
   background-color: #333;
}
.caas .recommendations-title-content{
   color: #d4d0d0;
   background: #333;
}
:after, :before{
   background: #3c3c3c;
}
.caas .navigation-header .dropdown ul.dropdown-menu li.dropdown-menu-item--customer-number, .caas .navigation-header .dropdown ul.dropdown-menu li a, .caas .navigation-header .dropdown ul.dropdown-menu li button{
   background-color: #333;
}
.caas *, .caas :after, .caas :before{
   color: #d6d6d6;
}
.caas .new-arrangement .product-price-now{
   color: #8a8787;
}
.caas .product-main-info .button-separator span{
   background-color: #3c3c3c;
}
.order .products-table .table-col .webtext .webtext1{
   color: #bfbfbf;
}
.order .products-table .table-col .webtext .webtext2.text{
   color: #929292;
}
}
