css:
@-moz-document domain("windows93.net") {
.ui_window--active .ui_window__head, .skin_background {
    background: #23272A;
}
.btn, button:not(.unstyled), input[type="reset"], input[type="submit"], input[type="button"], .ui_window, .ui_menu--bar .ui_menu__submenu ul, .ui_menu--menu ul, .ui_menu--menu .ui_menu__submenu ul {
    color: white !important;
    border-top: none !important;
    border-left: none !important;
    border-right: none !important;
    border-bottom: none !important;
    box-shadow: none !important;
    background-color: #2C2F33 !important;
    
}
* {
    color:white !important;
}
.ui_menu--bar .ui_menu__submenu ul, .ui_menu--menu ul, .ui_menu--menu .ui_menu__submenu ul, .skin_outset_deep, .ui_window {
    border-top: none !important;
    border-left: none !important;
}
.skin_base {
    background: #23272A !important;
}
.ui_explorer--not_workspace{
    background-color: #23272A
}
#s42_background {
    background-color: #23272A;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: 50% 50%;
}
#s42_notif {
    background-color: #23272A;
}
#s42_bios {
    background-color: #7289DA;
}
}
