@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wizaz.pl") {

html, body,td,th,div, span,.smallfont,td, th, p, li,#global,.navbar,.wysiwyg,.submit, label, input,select,#layout {font: 12px Tahoma normal !important;font-family: Tahoma !important;}
html, body, div, span, a, p, img, ul, ol, li, dl, dt, dd, table, form, fieldset, input, label, legend {font-family:Tahoma  !important}
label {text-align:right !important}
.intro {font-size:0.9em !important}
.tytul {font-size:1.1em !important;font-weight:bold !important}
.data {font-size:0.8em !important;color:#666 !important}
.kategoria a {font-weight:bold !important;font-size:0.85em !important; color:#555 !important}
.alt1Active a:link {color:#a91b6b !important}
.alt1,.alt2Active,.alt1Active {background:#fff !important }

.thead,.thead a {background:#bbc87f!important;color:#bbc87f!important;font-size:12px;font-size:0.9em !important;font-weight:bold !important}
.alt2 {background:#f5f8e6 !important}
.tcat a {font-size:14px !important}
#alt1 img  {display:none !important;-moz-opacity:0.5 !important}
.tcat,.vbmenu_control,.tfoot {background:#8b2e62 !important;border:none !important}
.smallfont {font-size:0.9em !important}
.body_alink {color:#a91b6b!important}
.menu_a a:visited, .menu_a a:active, .menu_a a:link {
font-size:11px !important ;
}
#sample_attach_menu_child a, #sample_attach_menu_child2 a, #sample_attach_menu_child3 a, #sample_attach_menu_child4 a, #sample_attach_menu_child5 a, #sample_attach_menu_child6 a, #sample_attach_menu_child7 a, #sample_attach_menu_child8 a, #sample_attach_menu_child9 a {
font-size:0.9em !important;
padding:8px 0 8px 10px !important

}
.navbar,.vbmenu_control {font-size:0.958em  !important}
.vbmenu_popup, .vbmenu_popup a {font-size:0.958em !important}
div table.tborder tbody tr td.alt2, .tborder tbody tr td.alt1Active,.tborder tbody tr td.alt1 {border-bottom:1px dotted #ddd !important}
#collapseobj_forumhome_activeusers {display:none}
#collapseobj_forumhome_birthdays {display:none}
}
