css:
@-moz-document url("https://share.dmhy.org/") {
@media(max-width: 768px) {
    .bg {
        min-width: inherit;
    }
    .quick_search {
        width: auto;
    }
    #keyword {
        width: auto;
        height: 30px;
    }
    .quick_search .formButton {
        height: 38px;
        font-size: 18px;
    }
    #topic_list tr td:nth-child(1), #topic_list tr th:nth-child(1), #topic_list tr td:nth-child(2), #topic_list tr th:nth-child(2), #topic_list tr td:nth-child(9), #topic_list tr th:nth-child(9) {
        display:none;
    }
    #topic_list tr td:nth-child(3), #topic_list tr th:nth-child(3) {
        font-size: 18px;
    }
    .arrow-magnet {
        background-size: 364px 102px;
        background-position: -34px -80px;
    }
    .download-arrow {
        font-size: 24px;
        padding-left: 16px;
        margin: -3px;
    }
}
}
