css:
@-moz-document url-prefix("https://www.netflix.com/") {
/* blur spoilers and title (watching) */
.title-and-synopsis,
.synopsis,
.nfa-w-60,
.nfa-pb-05-em,
.PlayerControls--button-control-row .video-title.text-control span {
    color: transparent;
    text-shadow: 0 0 15px rgba(0, 0, 0, 0.5);
}

/* blur spoilers and title (watch next) */
.WatchNext-episode-synopsis,
.WatchNext-episode-title {
    color: transparent;
    text-shadow: 0 0 15px rgba(0, 0, 0, 0.5);
}

/* blur spoilers and title (overview) */
.episodeLockup > .episodeSynopsis,
.current.episodeLockup > .episodeSynopsis,
.episodeTitle > .ellipsized,
.watched .episodeTitle b {
    color: transparent;
    text-shadow: 0 0 15px rgba(0, 0, 0, 0.5);
}

/* blur episode thumbnail (watching) */
.thumbnail {
    -webkit-filter: blur(10px);
    -moz-filter: blur(10px);
    -o-filter: blur(10px);
    -ms-filter: blur(10px);
}

/* blur episode thumbnail (overview) */
.video-artwork.episodeArt {
    -webkit-filter: blur(10px);
    -moz-filter: blur(10px);
    -o-filter: blur(10px);
    -ms-filter: blur(10px);
}

/* blur episode thumbnail (watch next) */
.WatchNext-autoplay {
    -webkit-filter: blur(10px);
    -moz-filter: blur(10px);
    -o-filter: blur(10px);
    -ms-filter: blur(10px);
}
}
