css:
/*
 * v2
 */

@-moz-document regexp("https://github.com/(dashboard)?(\\?.*)?") {
  .boxed-group.flush.js-repos-container,
  .github-jobs-promotion,
  .news.column.two-thirds,
  .octofication.js-notice,
  .repo-filterer,
  #your_teams {
    display: none;
  }
  .boxed-group.flush.repos.user-repos {
    width: 980px;
  }
  .boxed-group.flush.repos.user-repos * {
    font-size: 14px;
  }
  #your-repos-filter {
    padding: 8px 16px;
    margin-bottom: 10px;
  }
}

@-moz-document regexp("https://github.com/\\w+(\\?.*)?") {
  [title="Followers"],
  [title="Following"] {
    display: none;
  }
}
