css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("amoursucre.com"), domain("amordoce.com"), domain("sweetamoris.de"), domain("corazondemelon.es"), domain("flirttistoori.com"), domain("csabitasboljeles.hu"), domain("slodkiflirt.pl"), domain("sweetflirt.ro"), domain("sladkiiflirt.ru"), domain("askito-m.com"), domain("sweetcrush.co.uk"), domain("mycandylove.com")
{
/* Esses são os dominios que o style vai pegar, não mexer */
  
/* Fundo da página */   
  index > .connected
  {
    background-color: #FFFFFF !important;
    background-image: url("http://i63.tinypic.com/3461i77.png") !important;
    background-repeat: no-repeat !important;
    background-position: center center !important;
    background-attachment: fixed !important;
  }
/* Logo do Amor Doce*/
  #site-logo > a > img[src*="/logo-as.png"]
  {
    height: 0 !important;
    width: 0 !important;
    background: url("http://i66.tinypic.com/vxoz8o.png") no-repeat !important;
    padding-left: 427px !important;
    padding-top: 142px !important;
  }

  .halo
  {
    background-image: url(http://www.amoursucre.com/image/halo.png) !important;
  }

  panel-connected
  {
    background: #FFE4C4!important;
  }

  #pseudo-container > .blue
  {
    background: #FF7F50!important;
    box-shadow: 0 1px 1px rgba(125,125,125,0.75) !important;
  }

  .title-container a
  {
    color: #4EEE94!important;
    text-shadow: 0 1px 1px rgba(125,125,125,0.75) !important;
  }

  #episode-container
  {
    background: #87CEFF!important;
  }

  #episode-container span
  {
    color: #E066FF!important;
  }

  #episode-container .number
  {
    color: #FF8247!important;
  }

  .trombone
  {
    background-image: url("http://www.amoursucre.com/image/paper-clip/left.png") !important;
  }

  #currencies-container .values
  {
    background: #FF7F50 !important;
    border: 2px dotted #FFFF00!important;
    color: #4EEE94!important;
  }

  #panel-menu a
  {
    background: #BF3EFF!important;
    color: #FF7F24!important;
  }

  #panel-menu a:hover
  {
    background: #FF7F24!important;
    color: #BF3EFF!important;
  }

  navigation-menu > div > .pink, navigation-menu > div > .blue
  {
    background: #FFA500!important;
    color: #7FFFD4!important;
  }

  navigation-menu > div > .active, navigation-menu > div > a:hover
  {
    background: #7FFFD4!important;
    color: #FFA500!important;
  }

  navigation-menu > div > .border
  {
    border: none !important;
  }

  side-links > .link-entry
  {
    background: #FFA54F!important;
  }
}
