css:
@-moz-document domain("www.wykop.pl") {
body, body *
{font-family: "Open Sans"}
body
{background:#353535}
#site > div.wrapper
{background: #313233}
.grid
{width: 100%;
float: left;
background-color: #313233}
#site, .comments-stream
{background: #262626}
.comments-stream li
{background: #303031;
border: 0px}
#nav .nav li.active a
{background-color: #393939}
#nav
{background: #1b1b1b}
em.mark-number
{background: #3d3d3c}
#nav .nav li a
{color: #e1d7ce;
font-size: 12px}
#nav .nav li a:hover, #nav .nav li.active a
{color: #b2b2b2!important;
border-color: #313836!important;opacity: 1}
#nav li.notification p a:hover, .notification p a:hover
{color: #000 !important;}
#nav .notification a b, .notification a b
{box-shadow: 0 1px #600;
background: #f7590a;
text-shadow: 0 1px #900;
color: #fff;
border-radius: 3px;
text-align: center;
font-size: 11px}
.nav > ul li a
{font-size: 11px;
color: #9d9992}
.nav li.active a
{color:#c1c1c1;
background-color: #262626;
border-color:#4e4e4e}
.diggbox span:first-child, .rdiggbox
{color:#d8dddf;
font-size: 12px!important;
background: #6c6c6c;
border-radius: 4px}
.diggbox span.button.submit
{color:#d8dddf;
background: #6c6c6c!important;
font-size: 10px!important;
border-radius: 2px;
border:0px}
.diggbox a.dropdown-show
{color:#737e86;
font-size: 10px!important}
.diggbox.digout span:first-child
{color:#b7b7b7;
font-size: 12px!important;
background: #262626}
.diggbox.digout span:last-child
{color:#b7b7b7!important;
font-size: 10px!important;
background: #262626!important}
.r-block 
{background: #262626!important;border:0!important}
.r-block.screening-displace 
{display:none}
li, h4, ul
{border-color:#313131!important }
.article h2
{font-size: 14px!important}
a.link, a
{text-decoration: none;
color: #e1e1e1}
body a:hover
{color: #c8c8c8 !important}
.article h2 a:visited
{color: #777}
.create.tag:nth-of-type(1) > .affect
{color: #9d9d9c!important;
font-weight: bold;
font-style:normal!important}
.article > div p.text
{font-size: 13px!important}
.article > div p.text a
{color: #c6d2df}
.article > div p.text a:hover
{color: #b9b9b9!important;
border-bottom: 0px!important}
.lcontrast .affect
{color: #8a9083 !important}
.dC.preview.clearfix.article > .m-reset-margin.lcontrast > .elements.row > .affect:nth-of-type(1)
{font-size: 11px}
.rbl-block
{background: #303031;
border-left:0px;
border-right:0px;
border-bottom-right-radius:0px;
border-bottom-left-radius:0px;
border: 0px}
.nav .info
{color: #707070}
ul.related li > div div.content span
{background:#31302e}
.nav {border:0px}
ul.related li > div span.data > span b
{color: #c8bba8}
ul.related li > div div.content
{border:0px!important}
.grid-right > div
{color: #9b9b9b;background:#262626;
border:0}
.dark
{color: #9b915a}
.text p
{color: #c6d2df!important;
font-size: 13px!important}
.showTagSummary
{color: #dfd286 !important}
.voters-list *,.voters-list
{color: #757575 !important}
.author i.fa-plus, .c2a7017
{color: #60bb56;
font-size: 12px!important}
.author i.fa-minus, .cbb0000
{color: #ea645a;
font-size: 12px!important}
.showSpoiler, .show-more
{color:#d9d9d9!important;
background:#495a5d}
.media-content.too-long-pic .expand
{color:#a9b1a6!important;
background:#313836;
text-shadow:0px 0px}
.media-content .description
{background:#2c2c28;
border-top:0px;
font-size: 10px!important}
.icon-media.play, .icon-media.gif
{color: #fff!important}
.author, .author.hlight
{border-color:#464646!important}
.wblock textarea, .wblock form textarea
{color: #3b3b3b;
background:#cecbbf;
font-size: 13px;
border:0px;
border-radius:7px}
.mark-bg, .replyOn .wblock.replyForm
{background:#2a2a2a!important; border:0}
.button, .button_ddC, .reply fieldset.row.buttons .button a, a.editlenny + a.dropdown-show > .fa.fa-caret-down
{color: #c3c3c3;background:#3d3d3d!important}
.onEnter
{background:#3d3d3d!important;border:0px}
button.submit, .button.submit, input[type="submit"]
{color:#c3c3c3;background:#3d3d3d;
border:0}
label.inline, input[type="text"].create
{color:#9d9693;background: #3b3b3b}
blockquote p.description, .media-header p.description
{border:0}
.button
{color:#7b7b7b;
background: #8e9989;
font-size: 11px;
font-weight: normal;
border: 0px}
#footer h4, #footer a
{color: #5d625b}
#footer a
{color: #696a68}
#footer a.tag:hover
{background-color: #282a27; border:0px}
#footer
{background: #1b1b1b}
#wpladbottom
{display:none!important}
::selection
{background: #a6a399;
color: #1b1919}
#bmone2n-7069\.1\.1\.30, #bmone2n-7069\.4\.1\.36, #bmone2n-7069\.2\.1\.36, #bmone2n-7069\.3\.1\.36 {
display:none!important;
}
#bmone2n-7069\.1\.1\.36, #bmone2n-7069\.4\.1\.30, #bmone2n-7069\.2\.1\.30, #bmone2n-7069\.3\.1\.30 {
display:none!important;
}
#dyingLinksBox {
display:none!important;
}
}
