css:
@-moz-document url-prefix('http://chatango.com/login')
{

/*by Kohiruimaki */

/* the background i put from other website thx for author for the bg */
/* happy fun */

.container {
    margin: auto;
        margin-top: auto;
        margin-left: auto;
    min-width: 360px;
    max-width: 200px;
    padding: 10px;
    text-align: center;
    background-position: center center;
    display: block;
    margin-top: 75px;
    margin-left: 540px;
    background-color: rgba(28, 27, 27, .15);
}
*[class="desktop"] {
    background-image: url(https://i.imgur.com/45teeuz.png);
    background-size: cover !important;
  background-attachment: fixed !important;
  background-position: center center !important;
}

/*login box */
#loginbox input {
    width: 100%;
    margin-bottom: 20px;
}

#loginbox input[type="text"], #loginbox input[type="password"] {
    border: none;
    border-bottom: 1px solid #fff;
    background: transparent;
    outline: none;
    height: 40px;
    color: #fff;
    font-size: 16px;
}
#loginbox input[type="submit"] {
    border: none;
    outline: none;
    height: 40px;
    color: #fff;
    font-size: 16px;
    background: rgb(255, 38, 126);
    cursor: pointer;
    border-radius: 20px;
    text-align: center;
}
#loginbox input[type="submit"]:hover {
    background: #f90c6c;
    color: #262626;
}
#loginbox a {
    color: #fff;
    font-size: 14px;
    font-weight: bold;
}
#loginbox input[type="checkbox"] {
    color: #000;
    background: rgba(252, 73, 73, 0.9);
}

input[type="checkbox"] + label span {
    display: inline-block;
    vertical-align: text-bottom;
    width: 1.25em;
    height: 1.25em;
    margin-right: .3em;
    border: 1px solid #F51515;
    background: rgba(252, 73, 73, 0.4);
}
body.desktop .bottombar.has-border {
    background: rgba(28, 27, 27, .15);
}
body.desktop .bottombar.has-border {
    border-top: 1px solid #454444;
    padding-top: .5em;
}
.uppermenu, .top-links a {
    color: #fff;
    font-size: 14px;
    font-weight: bold;
}
img#LOGO {
    opacity: 0.6;
}
}
