css:
@-moz-document domain("myanimelist.net") {

    /* COLORS */

    /** DESCRIPTION : change the elements of the colors page **/
    :root {
        --body-bg: #1E1E1E !important; 
        --card-bg: #292929 !important;
        --card-bg-2: #212121 !important;
        --header-bg: #4F74C8 !important; 
        --border-color: #000 !important;
        --primary-color: #FFF !important;
        --secondary-color: #EBEBEB !important;
        --invert: invert(1) !important;
    }

    /* GENERAL */

    /** DESCRIPTION : change the elements of all page **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド";
    }

    *::-moz-focus-inner {
        border: 0 !important;
    }

    a {
        color: #3966CE !important;
    }

    select {
        -webkit-appearance: none;
        -moz-appearance: none;
        text-indent: 0px;
        text-overflow: ellipsis;
        white-space: nowrap;
    }

    body:not(.ownlist) {
        background: var(--body-bg) !important;
    }

    #myanimelist {
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .15) !important;
        margin-top: 50px !important;
    }

    #contentWrapper {
        margin-top: 0px !important;
    }

    .manga-store .content-detail-bottom,
    .manga-store .manga-store-search-results .items,
    .ownlist_style .style-setting .style-setting-form-button,
    .page-common .border_top,
    .page-episode-add table.episode-edit,
    .page-forum .sig,
    .spaceit.textReadability.word-break,
    ul.ignored-user-list {

        /*** BORDERS TOP ***/
        border-top: 1px dotted var(--border-color) !important;
    }

    .page-common .borderDark {
        border-color: var(--border-color) !important;
        border-style: dotted;
    }

    .content-container .container-left,
    .manga-store .main-left,
    .page-common .profile_leftcell,
    .page-common #content.container--small-left .left-column,
    .news-list .comment-list .information .tags .tags-inner .tags-inner-attributes,
    .news-list .news-unit .information .tags .tags-inner .tags-inner-attributes,
    .search_all .content-result .content-left {

        /*** BORDERS RIGHT ***/
        border-right: 1px dotted var(--border-color) !important;
    }

    .anime-manga-search .genre-link .genre-list,
    .detail-preview-container::after,
    .featured-content-block-outer,
    .forum .forum-side-block .forum-side-list .forum-post,
    .login-form-header-text,
    .manga-store .comics-list,
    .manga-store .comic-detail-info,
    .manga-store .detail-card .detail-infoList-item.copyright,
    .manga-store .section-title,
    .manga-store .view-all,
    .manga-store .detail-card .detail-title,
    .manga-store .detail-card .detail-body,
    .membership .membership-payment .form-table.border-bottom,
    .menu-category-tags .btn-category .icon-category-svg,
    body.news .news-content-block.news-tags h2.news-tags-header,
    body.news .news-content-block .news-related-database tr td,
    body.news .news-side-block .news-list .news,
    body.news .news-side-block .news-list .featured,
    body.news .news-side-block .featured-list .news,
    body.news .news-side-block .featured-list .featured,
    body.news .featured-side-block .news-list .news,
    body.news .featured-side-block .news-list .featured,
    body.news .featured-side-block .featured-list .news,
    body.news .featured-side-block .featured-list .featured,
    body.notification .notificaiton-category-nav li,
    body.notification .notification-header h3,
    body.notification .notification-item,
    .page-review .score_top,
    .search_all .content-result .list,
    .slider-wrap,
    .total_messages {

        /*** BORDERS BOTTOM ***/
        border-bottom: 1px dotted var(--border-color) !important;
    }

    .featured-pickup .featured-pickup-unit .information .pv,
    .featured-pickup .featured-pickup-unit .information .tags,
    .manga-store .main-right,
    .news-list .comment-list .information .pv,
    .news-list .news-unit .information .pv,
    .news-list .comment-list .information .tags,
    .news-list .news-unit .information .tags {

        /*** BORDERS LEFT ***/
        border-left: 1px dotted var(--border-color);
    }

    .borderClass.pl0.pr0.pt16.pb0.cl-b,
    .ranking-digest.left li.ranking-unit {

        /*** BORDERS NONE ***/
        border: none !important;
    }

    .btn-choose-your-own {

        /*** CHOOSE YOUR OWN BUTTON ***/
        background: #4F74C8;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
        border: none;
        border-radius: 2px;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
        padding: 8px 12px !important;
    }

    .fa.fa-chevron-right {
        color: #FFF !important;
    }

    .anime-slide-block .anime-slide .btn-anime .link .users,
    .anime-detail-header-video .manga-store-preview,
    .btn-category .icon-category-svg,
    .btn-detail-recommendations-view-all.fl-r,
    .btn-forum-manga-store,
    .btn-mal-service,
    .content-right iframe,
    .detail-preview-container a:nth-of-type(2),
    div[style="overflow: hidden; padding: 10px 0; border-bottom: solid #cdcdcd 1px"],
    .fa-cog,.fa-plus-square-o,
    .footer-ranking,
    .forum_boardrowspacer,
    .gauge-outer,
    .icon-about-recommendations,
    .icon-watch,
    .icon-watch-pv,
    .index-manga-store-description,
    .index-watch-description,
    .js-sns-icon-container,
    .manga-store-search-genres,.page-common .border_bottom,
    .page-common #footer-block,
    .page-common #headerSmall .banner-header-anime-straming,
    div[id*="bottom_rec_"],
    .swiper-container.manga-store-information-carousel-container.swiper-container-horizontal,
    .video-providers,
    .watch-description.di-b,
    .widget.left_bottom_ad.left {
        display: none !important;
    }
    /* DIALOG */

    /** DESCRIPTION : change the elements of the dialog **/
    #dialog,
    .modal-container,
    .bgbdrContainer {
        background: var(--card-bg-2) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        padding: 10px 20px 30px 20px;
    }

    #dialog {
        margin-top: 40px !important;
    }

    #dialog td {
        border: none !important;
    }

    #dialog .normal_header {
        border-bottom: 1px dotted var(--border-color);
        border-width: 0px 0px 1px 0px;
    }

    #dialog *,
    .modal-container * {
        color: var(--primary-color);
        font-family: "Roboto";
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: normal;
    }

    #dialog label,
    #dialog .ff-helvetica {
        font-size: 14pt !important;
    }

    #dialog input,
    #dialog select,
    #dialog textarea {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-weight: normal !important;
    }

    #dialog textarea {
        padding: 10px !important;
    }

    #dialog .inputButton {
        background: #3966CE !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        padding: 8px 10px !important;
        margin-top: 8px !important;
    }

    #dialog .separator {
        display: none;
    }

    #dialog #hide-advanced-button,
    #dialog #show-advanced-button {
        background-color: transparent !important;
        color: var(--primary-color);
    }

    .modal-payment-method .border-bottom {
        border-bottom: 1px dotted var(--border-color) !important;
    }

    .modal-container .title {
        font-size: 16pt !important;
    }

    .page-common .badresult,
    .goodresult,
    .successQuery {
        background: var(--card-bg-2) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: var(--secondary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .badresult.badresult--is-reset-password {
        margin-top: 30px;
    }

    .badresult.badresult--is-reset-password ol li ul li {
        height: 100% !important;
        width: 100% !important;
        overflow: hidden !important;
    }

    #dialog .goodresult,
    #dialog .successQuery {
        background: var(--card-bg) !important;
    }

    .icon-social-login {
        color: #FFF !important;
        line-height: 22px !important;
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/

    #headerSmall {

        /*** HEADER CONTAINER ***/
        background: no-repeat url("https://i.imgur.com/lS9bEN0.jpg") center 40% !important;
        background-size: cover !important;
        border-top-right-radius: 2px !important;
        border-top-left-radius: 2px !important;
        height: 50px !important;
    }

    #headerSmall .link-mal-logo {

        /*** HEADER LOGO ***/
        background: no-repeat url("https://i.imgur.com/qSZfkkm.png") center !important;
        background-size: 100px auto !important;
        filter: invert(1) !important;
        margin-top: 3px !important;
        margin-left: -50px !important;
    }

    #headerSmall #header-menu {
        top: 16px !important;
        right: 16px !important;
    }

    .fa-bell::before,
    .fa-caret-down::before,
    .fa-envelope::before,
    .fa-list::before,
    .fa-tags::before {

        /*** HEADER ICONS ***/
        content: "" !important;
    }

    #headerSmall #header-menu .header-list-button {

        /*** HEADER ICONS LIST ***/
        background: no-repeat url("https://i.imgur.com/fz6600P.png") center !important;
        background-size: 15px auto !important;
        border-radius: 1px !important;
        filter: invert(1) !important;
    }

    #headerSmall #header-menu .header-message-button {

        /*** HEADER ICONS MESSAGE ***/
        background: no-repeat url("https://i.imgur.com/5LC44P7.png") center !important;
        background-size: 20px auto !important;
        border-radius: 1px !important;
        filter: invert(1) !important;
    }

    #headerSmall #header-menu .header-notification-button {

        /*** HEADER ICONS NOTIFICATION ***/
        background: no-repeat url("https://i.imgur.com/aqaUYbG.png") center !important;
        background-size: 22px auto !important;
        border-radius: 1px !important;
        margin-right: 10px !important;
    }

    .page-common .header-notification-button.has-unread::after {
        border-radius: 50% !important;
        border: none !important;
        font-size: 12pt !important;
        height: 15px !important;
        width: 15px !important;
    }

    .page-common .header-notification-item {
        border: none !important;
    }

    .page-common .header-notification-item {
        background: var(--card-bg) !important;
        border: none !important;
    }

    #headerSmall #header-menu .border {

        /*** HEADER BORDERS ***/
        padding-left: 0px !important;
        visibility: hidden;
    }

    #headerSmall #header-menu .header-menu-unit {

        /*** HEADER PROFILE BUTTON ***/
        border-top-left-radius: 2px !important;
        border-bottom-left-radius: 2px !important;
    }

    #headerSmall #header-menu .header-menu-unit a {
        font-size: 12pt;
        font-stretch: condensed !important;
    }

    #headerSmall #header-menu .header-profile-button {
        border-top-right-radius: 2px !important;
        border-bottom-right-radius: 2px !important;
        box-shadow: none !important;
    }

    #headerSmall .fa.fa-caret-down {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center !important;
        background-size: 10px auto !important;
        padding: 10px 0px 3px 10px !important;
    }

    .header-menu-dropdown.header-profile-dropdown.arrow_box,
    .page-common .header-notification-dropdown,
    .page-common .header-list .header-list-dropdown {
        background: var(--card-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .page-common .header-notification-dropdown .arrow_box {
        border: none !important;
        border-radius: 2px !important;
    }

    .header-menu-dropdown.header-profile-dropdown.arrow_box a {
        font-size: 14pt !important;
    }

    .page-common .header-profile .header-profile-dropdown ul li a,
    .page-common .header-notification-dropdown-inner h3,
    .header-notification-item-list,
    .page-common .header-list .header-list-dropdown li a {
        background: var(--card-bg-2) !important;
        border-bottom: 1px dotted var(--border-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    .page-common .header-profile .header-profile-dropdown ul li:last-of-type a,
    .page-common .header-list .header-list-dropdown ul li:last-of-type a {
        border-bottom: none !important;
    }

    .page-common .header-notification-dropdown-inner h3 {
        padding: 10px;
    }

    .header-notification-dropdown-inner * {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .page-common .header-notification-view-all a {
        background: var(--card-bg);
        border-top: none;
    }

    .header-menu-login #malLogin,
    .header-menu-login .btn-signup {
        background: #4165BA !important;
        border: none !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 8px 4px !important;
        margin-top: -2px !important;
    }

    /* NAVIGATION */

    /** DESCRIPTION : change the elements of the navigation bar **/

    .page-common #menu {
        height: 50px;
    }

    .page-common #menu #menu_left {
        padding-left: 10px !important;
    }

    .page-common #menu #menu_left #nav .small {

        /*** NAVIGATION LINKS ***/
        font-size: 14pt !important;
        height: 50px !important;
        line-height: 25px !important;
    }

    .page-common #menu #menu_left #nav li a {
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        height: 40px !important;
    }

    .page-common #nav li.hover a,
    .page-common #nav li a:hover {
        background: #2E51A2 !important;
    }

    .page-common .wider li,
    .page-common li > ul > li {
        width: 200px !important;
        padding-bottom: 0px !important;
        height: 40px !important;
    }

    .forum_boardrow1 li > ul > li {
        width: 90% !important;
        margin-bottom: 10px;
    }

    .page-common #searchBar .form-select-outer {

        /*** NAVIGATION SEARCH SELECT ***/        
        height: 45px !important;
        overflow: visible !important;
        margin-top: -5px;
    }

    .page-common #searchBar #topSearchValue {

        /*** NAVIGATION SEARCH BAR ***/
        background: transparent !important;
        border: none !important;
        color: #FFF !important;
        font-family: "Roboto" !important;
        font-stretch: condensed;
        font-weight: bold !important;
        font-size: 14pt !important;
        height: inherit !important;
    }

    .page-common #topSearchText {
        background: transparent !important;
        color: #FFF !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        height: 29px !important;
        margin-top: 0px !important;
    }

    .page-common #topSearchButon {
        background: transparent !important;
        border: none !important;
        height: 34px !important;
        width: 42px !important;
        margin-left: -20px !important;
    }

    .page-common #topSearchResultList,
    .page-common .incrementalSearchResultList {
        background: var(--card-bg) !important;
        border: none !important;
        border-radius: 1px !important;
        text-align: center !important;
    }

    .page-common .incrementalSearchResultList .list {
        border-bottom: 1px dotted var(--border-color);
        font-size: 14pt !important;
        font-stretch: condensed;
        padding: 10px !important;
    }

    .incrementalSearchResultList .list.anime,
    .incrementalSearchResultList .list.character,
    .incrementalSearchResultList .list.club,
    .incrementalSearchResultList .list.manga,
    .incrementalSearchResultList .list.person,
    .incrementalSearchResultList .list.user {
        background: var(--card-bg) !important;
        border-radius: 1px !important;
    }

    .incrementalSearchResultList .list.focus {
        background: var(--card-bg-2) !important;
    }

    .incrementalSearchResultList .list.focus .name {
        font-weight: normal !important;
        margin-bottom: 20px !important;
    }

    .incrementalSearchResultList .list .image {
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .page-common .incrementalSearchResultList .list .off .info .media-type,
    .page-common .incrementalSearchResultList .list .on .info .media-type,
    .page-common .incrementalSearchResultList .list .on .info .extra-info {
        color: var(--secondary-color);
        font-size: 12pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
        line-height: 16pt !important;
    }

    .page-common .incrementalSearchResultList .list .name {
        margin-bottom: 6px;
    }

    .page-common .incrementalSearchResultList .list.anime .name,
    .page-common .incrementalSearchResultList .list.manga .name {
        height: 19px;
    }

    .separator {
        background: var(--card-bg) !important;
        border-radius: 1px !important;
        border-bottom: 1px dotted var(--border-color);
        color: var(--primary-color) !important;
        font-size: 12pt !important;
        font-stretch: condensed;
        padding-bottom: 10px;
        text-transform: uppercase !important;
        margin-bottom: -1px !important;
    }

    .separator span {
        line-height: 14px !important;
        margin-left: 3px !important;
    }

    .incrementalSearchResultList .list-bottom {
        background: var(--card-bg-2) !important;
        border-radius: 1px !important;
    }

    /* TITLE HEADER */

    /** DESCRIPTION : change the elements of the title header ***/

    .page-common #contentWrapper .h1 {
        background: #12130F !important;
        border: none !important;
        color: #FFF !important;
        font-stretch: condensed !important;
        font-family: "Roboto" !important;
        font-size: 16pt !important;
        font-weight: normal;
        padding: 15px 15px 18px 18px !important;
    }

    .page-common #contentWrapper .h1 * {
        color: #FFF !important;
    }

    .page-common #contentWrapper .header-right {
        color: #FFF !important;
        font-family: "Roboto" !important;
        font-stretch: condensed !important;
        font-size: 14pt !important;
        font-weight: normal;
        margin-top: 20px !important;
        margin-right: 20px !important;
    }

    .page-common #contentWrapper .header-right a {
        color: #FFF !important;
        font-weight: normal;
    }

    .page-common #contentWrapper .header-right .fa.fa-cog {
        margin-right: 10px !important;
    }

    /* HOME */

    /** DESCRIPTION : change the elements of home page **/

    .page-common #content {

        /*** HOME CONTENT ***/
        background: var(--card-bg) !important;
        border: none !important;
        padding: 0px 18px 0px 0px !important;
    }

    .page-common #contentWrapper {
        background: var(--card-bg) !important;
    }

    .index #content .left-column {

        /*** HOME CONTENT LEFT ***/
        border-right: 1px dotted var(--border-color) !important;
    }

    #content .left-column {
        padding-left: 20px !important;
    }

    .page-common #content .widget .widget-header {

        /*** HOME CONTENT LEFT WIDGET HEADER ***/
        color: var(--primary-color);
        border-bottom: 1px dotted var(--border-color);
        font-size: 16pt !important;
        font-stretch: condensed !important;
        font-weight: normal;
        margin-bottom: 15px !important;
    }

    .page-common #content .widget .widget-header a {
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .page-common #content .left-column .widget .title,
    .page-common #content .left-column .widget .spaceit,
    .page-common #content .left-column .widget .spaceit *,
    .video-promotion .video-unit .title,
    .video-block .video-list .title,
    .anime-slide-block .anime-slide .btn-anime .link .title {

        /*** HOME CONTENT LEFT WIDGET TITLE ***/
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal;
    }

    .page-common #content .left-column .widget .spaceit {
        color: var(--primary-color);
    }

    .page-common #content .left-column .widget .text,
    .page-common #content .left-column .widget .textReadability {

        /*** HOME CONTENT LEFT WIDGET TEXT ***/
        color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .page-common #content .left-column .widget .information *,
    .page-common .lightLink {

        /*** HOME CONTENT LEFT WIDGET INFORMATION  ***/
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .page-common #content .left-column .widget .information p {
        color: var(--secondary-color) !important;
    }

    .page-common .picSurround a,
    .picSurround img,
    .widget-slide-block .widget-slide .btn-anime,
    .widget-content .items .item,
    #widget-manga-store .widget-slide .btn-anime .link {

        /*** HOME CONTENT LEFT WIDGET COVER ***/
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .page-common .picSurround img {
        background-color: transparent !important;
        border: none !important;
        padding: 0 !important;
    }

    #widget-manga-store .widget-slide-outer {
        height: 195px !important;
    }

    #widget-promotional-video .widget-slide-outer {
        height: 195px !important;
    }

    #v-auto-recommendation-personalized_anime .items .item {
        border: none !important;
    }

    .widget-slide-block .widget-slide .btn-anime .external-link {
        background: var(--card-bg-2);
        box-shadow: none !important;
        font-size: 14pt !important;
        font-stretch: condensed;
        overflow: visible !important;
    }

    .page-common #v-auto-recommendation-personalized_anime {
        padding-bottom: 15px !important;
    }

    .page-common #v-auto-recommendation-personalized_manga * {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .page-common #widget-manga-store,
    .page-common .widget-slide-outer {
        width: 100% !important;
    }

    .page-common #content .news-unit-right,
