        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
        padding: 5px 8px !important;
        margin-bottom: 4px !important;
    }

    .news-side-block .thumbs,
    .featured .thumbs,
    .columnists-unit-side .image {

        /*** NEWS RIGHT COLUMN BLOCK CONTENT COVER ***/
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .news .news-breadcrumbs {

        /*** NEWS ARTICLE HEADER ***/
        border-bottom: none !important;
    }

    .news-container .title,
    .news-container h1 {
        color: var(--primary-color) !important;
    }

    .news-container h1 {
        font-size: 18pt !important;
    }

    .news-container .summary {
        color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        margin-bottom: 20px;
    }

    .news .news-info-block {

        /*** NEWS ARTICLE INFORMATION BLOCK ***/
        background: var(--card-bg-2) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding: 15px 10px !important;
    }

    .news .news-info-block * {
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .news .news-container h3 {

        /*** NEWS ARTICLE SUB HEADERS ***/
        border-bottom: 1px dotted var(--border-color);
        color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 16pt !important;
        font-stretch: condensed !important;
        font-weight: normal;
        padding-bottom: 5px !important;
        margin-bottom: 10px !important;
    }

    .news .news-container h4 {
        color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal;
    }

    .news .news-container hr {

        /*** NEWS ARTICLE SEPARATORS ***/
        display: none !important;
    }

    .news .news-container .content,
    .featured-article-body p {

        /*** NEWS ARTICLE CONTENT ***/
        color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    .news .news-container .tags {

        /*** NEWS ARTICLE TAGS CONTAINER ***/
        background: transparent !important;
        border-top: 1px dotted var(--border-color) !important;
        padding-top: 15px !important;
    }

    .news .content-left h2 {

        /*** NEWS ARTICLE COMMENTS HEADER ***/
        color: var(--primary-color);
        font-family: "Roboto" !important;
        font-size: 16pt !important;
        font-stretch: condensed !important;
    }

    .news .news-list .comment-list {

        /*** NEWS ARTICLE COMMENTS LIST ***/
        background: var(--card-bg-2) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding: 15px 10px 18px 10px !important;
        max-width: 690px !important;
        margin-bottom: 15px !important;
    }

    .news .news-list .comment-list .text {

        /*** NEWS ARTICLE COMMENTS LIST CONTENT ***/
        color: var(--primary-color);
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .news .btn-form-submit.mid {
        color: #FFF !important;
        font-size: 14pt !important;
        font-weight: normal;
    }

    .news-list .comment-list .quotetext {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color);
        padding: 10px 15px !important;
    }

    .button.show_button,
    .button.expand_quote {
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 5px 8px !important;
    }

