@-moz-document regexp("https://myanimelist.net/(character|people).*") {

    /* CHARACTER/PEOPLE DETAIL */

    /** DESCRIPTION : change the elements of the character/people detail page **/
    #myanimelist > .wrapper > #contentWrapper > #content > table .borderClass {
        padding-left: 8px !important;
    }

    .borderClass[style="border-width: 0 1px 0 0;"] {
        padding-right: 7px !important;
    }

    .page-common .normal_header {
        border-bottom: 1px dotted var(--border-color) !important;
        border-width: 0px 0px 1px 0px !important;
        color: var(--primary-color);
        font-size: 16pt !important;
    }

    .page-common .normal_header[style="height: 15px;"] {
        height: 25px !important;
    }

    .page-common #myanimelist .wrapper #contentWrapper #content table tbody tr td[style="padding-left: 5px;"] {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding-left: 10px !important;
    }

    .page-common #myanimelist .wrapper #contentWrapper #content .borderClass,
    .page-common #myanimelist .wrapper #contentWrapper #content .borderClass * {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        line-height: 17pt !important;
    }

    .textarea {
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 10px !important;
    }

    .spaceit_pad .inputButton {
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        padding: 8px !important;
    }

}

@-moz-document regexp("https://myanimelist.net/about.php.*") {

    /* STAFF */

    /** DESCRIPTION : change the elements of the staff page **/
    table {
        margin-left: -32px;
    }

    table tr td {
        background: transparent !important;
    }

    /* SUPPORT */

    /** DESCRIPTION : change the elements of the support page **/
    #content.pt16,
    #content.pt16 * {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .js-btn-support-form {
        font-weight: normal !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

}

@-moz-document regexp("https://myanimelist.net/info.php.*") {

    /* INFO */

    /** DESCRIPTION : change the elements of the info page **/
    .page-common * {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

    .page-common .normal_header {
        border-bottom: 1px dotted var(--border-color);
        border-width: 0px 0px 1px 0px !important;
        margin-bottom: 20px;
    }

}
