css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https?://(?!softserve).*8ch.net.*'), regexp('.*oxwugzccvk3dk6tj.onion.*')
{
    * {
        font-family: "Droid Sans", "FontAwesome" !important;
        font-size: 10pt !important;
    }
  body
  {
                        
	background-repeat: no-repeat !important;
	background-position: bottom right !important;
	background-attachment: fixed !important;
	background-size: 250px;
    background: #17181A;
    color: #CECFD1 !important;
    border: 0 !important;
                        margin-left: 20px !important;
  }

    
    .quote { color: #a4e35a !important; }
    .postertrip { color: #d54a56 !important; }
    .name { color: #CECFD1 !important; }
    .dateTime { /*color: #5f656b !important;*/ }
    .subject { color: #f4aa5c !important; }
	    a:link, a:visited,
    #content .button, a.replylink:not(:hover),
    #boards a, #post-menu ul li,
    .yotsuba_b_new .backlink a {
        color: #8c86fc !important;
        /*font-size: 10pt !important;*/
        text-decoration: none !important;
    }

    .body, .omitted { margin-left:30px !important; }
    .post.op.has-file.body-not-empty { margin-bottom: 30px !important; }
    
    p.fileinfo {
        opacity: 0 !important;
        max-height: 0px !important;
        transition: .4s ease-out !important;
    }
    .files:hover p.fileinfo,
    .files.image-expanded p.fileinfo {
        opacity: 1 !important;
        max-height: 50px !important;
    }
    
    .post-btn {
        position: relative !important;
        top: -2px !important; left: -1px !important;
        width: 10px !important; height: 6px !important; font-size: 6pt !important;
    }
  .content_menu
  {
    background-color: #17181A;
  }

  .button
  {
    background-color: #17181A;
  }

  header div.subtitle, h1
  {
    color: #8c86fc;
  }

  a:link, a:visited, p.intro a.email span.name, a#catalog_search_button
  {
    color: #8c86fc;
  }

  a:hover,a:link:hover, a#catalog_search_button:hover
  {
    color: #ea75bc;
    text-decoration: underline;
  }

  a.post_no
  {
    color: #8c86fc !important;
  }

  a.post_no:hover
  {
    color: #ea75bc !important;
  }

  div.banner
  {
    background-color: #27282B;
  }

  div.post.reply
  {
    background-color: #17181A;
    border-width: 0px;
    border-style: solid;
    border-left-color: #383838;
    border-top-color: #383838;
    border-right-color: #282828;
    border-bottom-color: #282828;
  }

  div.post.reply.highlighted
  {
    background-color: #27282B;
  }

  div.post.reply div.body a
  {
    color: #7cafc2;
  }

  div.post.reply div.body a:hover
  {
    color: #27282B;
  }

  div.post-hover
  {
    background: #27282B !important;
    box-shadow: none !important;
  }


  p.intro span.subject
  {
    color: #ba8baf;
  }

  p.intro span.name
  {
    color: #d8d8d8;
  }

  .quote
  {
    color: #a1b56c;
  }



  form table tr th
  {
    background: #27282B;
    color: #d8d8d8;
  }

  div.ban h2
  {
    background: #17181A;
    color: #d8d8d8;
  }

  div.ban
  {
    border-color: #ba8baf;
    background: #17181A;
  }

  div.pages
  {
    background: #17181A;
    border-color: #181818;
  }

  div.pages a.selected
  {
    color: #86c1b9;
    font-weight: 700;
  }

  .desktop-style div.boardlist:not(.bottom)
  {
    background-color: #17181A !important;
    color: #d8d8d8;
  }

  div.boardlist:nth-of-type(1)
  {
    border-bottom: 1px solid #111 !important;
    box-shadow: 0 0 3px 0 #111;
  }

  div.boardlist a
  {
    color: #8c86fc;
  }

  hr
  {
    background-color: #17181A;
    border: 0;
    height: 1px;
  }

  div#options_div
  {
    background-color: #17181A;
  }

  div.options_tab_icon
  {
    color: #aaa;
  }

  div.options_tab_icon:hover
  {
    background-color: #111;
  }

  div.options_tab_icon.active
  {
    color: #ab4642;
  }

  div.blotter
  {
    color: #8c86fc;
  }

  span.omitted
  {
    color: #707070;
  }

  p.intro a,span.omitted a
  {
    text-decoration: none;
  }

  form#quick-reply
  {
    padding-right: 1px;
    border: 1px solid #111;
  }

  span.capcode
  {
    background-color: #000;
    padding: 2px 5px;
    border-radius: 10px;
  }

  div#watchlist
  {
    background-color: #27282B;
  }

  .theme-catalog div.thread:hover
  {
    background: #3b3b3b;
  
  }

  pre.prettyprint
  {
    padding: 4px;
    border: 1px solid #585858;
  }

  .intro span.subject
  {
    color: #7cafc2;
  }

  .intro span.name
  {
    color: #a1b56c;
  }

  #alert_div
  {
    background-color: #17181A;
  }

/* Code */
  .str
  {
    color: #a1b56c;
  } /* string */
  .kwd
  {
    color: #ba8baf;
    font-weight: bold;
  }

  .com
  {
    color: #585858;
  } /* comment */
  .typ
  {
    color: #86c1b9;
  } /* type */
  .lit
  {
    color: #f7ca88;
  } /* literal */
  .pun,.opn,.clo
  {
    color: #e8e8e8;
  }    /* punctuation */
  .pln
  {
    color: #e8e8e8;
  }    /* plaintext */
  .tag
  {
    color: #7cafc2;
    font-weight: bold;
  } /* html/xml tag */
  .atn
  {
    color: #bdb76b;
    font-weight: bold;
  } /* attribute name */
  .atv
  {
    color: #ffa0a0;
  } /* attribute value */
  .dec
  {
    color: #98fb98;
  } /* decimal */

/* Home Page */
  .box
  {
    background: #17181A !important;
    border-color: #383838;
    color: inherit;
  }

  .box-title
  {
    background: #17181A;
    color: #181818;
  }

/* Board Table */
  table tbody tr:nth-child(even),
table.board-list-table tr:nth-of-type( even ) .board-tags .board-cell
  {
    background: #17181A;
  }

  table.board-list-table .board-tags .board-cell:hover
  {
    background: #17181A;
  }

  table thead th
  {
    background: #17181A;
    color: #181818;
  }

  tbody.board-list-omitted td
  {
    background: #17181A;
  }

/* Velocious Threads */
  .box-content > .col:nth-child(odd)
  {
    background-color: #17181A !important;
  }

/* Inputs */
  input[type="text"], input[type="password"], textarea
  {
    background: #17181A;
    border: 1px solid #383838;
    color: #d8d8d8;
  }
}
