css:
@-moz-document url-prefix("https://lyrania.co.uk/game.php") {
	/* I am new to CSS i believe this is a good first attempt */
	/*Use Stylus add on if you want to edit CSS Chrome or Firefox perfered*/

input, textarea
{
	/* #000000 is all hex colors */
	/* background rgba is for the middle buttons attack,stop,continue etc. behind the text in this area. Allows RGB colors for 1st,2nd,3rd numbers then 4th is transparency*/
	background: rgba(38, 38, 38, 0.8);
    /*Border is 1 pixel surrounding it's area(middle window)*/
	border: 1px solid #F0FFF0;
    /*Text color of window*/
	color:#F0FFF0;
}
    /*Same Rules apply below with background rgba,border, and color*/
	/*Effects drop down menus*/
select
{
	background: rgba(38, 38, 38, 0.8);
	border: 1px solid #F0FFF0;
	color:#F0FFF0;
}
	/*The background rests here*/
body
{
	margin-right: auto;
    margin-left:  auto;
    padding-right: 10px;
    padding-left:  10px;
	color:#F0FFF0;
    background: url('/*[[bg]]*/') no-repeat center center fixed; /*Replace imgur with your own background url if you like */
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    height: 100%;
    overflow: auto;
}
	/*Effects Interactive Link text color on the Website Quests,Equipment,Name,Players Online etc.*/
	/*Effects Interactive Link text when hovering cursor over them*/
a:hover 
{
	color:#FFFF00;
	background: rgba(38, 38, 38, 0.8);
}   
	/*Effects top section*/
#header 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects middle section*/
#main 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects left section*/
#side1 
{
	
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects right section*/
#side2 
{
    border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
    padding: 0px 0px 0px 6px !important;
}
    /*Effects equipment upgrade section*/
#equipstuff
{
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects outer chat section*/
#chat 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects inner chat section*/
#chatwindow 
{
    /*Effects Time and Message text in chat color*/
	color: #FFFF00;
	border: solid #F0FFF0 1px;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects chat entry box section*/
#inputchat 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects chat button section*/
#chatbutton 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects stat and drop popup*/
#bannerholder 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
	/*Effects Quest popup window*/
#popupholder 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.5);
}
/*Effects Quest popup window*/
#popuptopbar 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.8);
}
/*Effects Quest popup window*/
#popuprescontainer 
{
	border: 1px solid #F0FFF0;
    background: rgba(38, 38, 38, 0.5);
}
	/*General Chatname color*/
.chatname 
{
	color:#CCCCCC;
}
	/*Assume it effects admins name*/
.admincolour
{
	color:#FFFF33;
}
}
