css:
@-moz-document domain("www.wykop.pl") {
body, body *
{font-family: "Verdana"}
body
{background:#353535}
#site > div.wrapper
{background: #202225}
.grid
{width: 100%;
float: left;
background-color: #202225}
#site, .comments-stream
{background: #202225}
.comments-stream li
{background: #36393e;
border: 0px}
.rbl-block
{background: #36393e;
border-left:0px;
border-right:0px;
border-bottom-right-radius:0px;
border-bottom-left-radius:0px;
border: 0px}
.grid-right > div
{color: #8490a8;background:#36393e;
border:0}
.dark
{color: #8490a8}
.r-block 
{background: #36393e!important;border:0!important}
.r-block.screening-displace 
{display:none}
li, h4, ul
{border-color:#40515b!important }
#nav .nav li.active a
{background-color: #2c363d}
#nav
{background: #24272c}
em.mark-number
{background: #344146}
#nav .nav li a
{color: #96a5b2;
font-size: 12px}
#nav .nav li a:hover, #nav .nav li.active a
{color: #c3cece!important;
border-color: #3cae8e!important;opacity: 1}
#nav li.notification p a:hover, .notification p a:hover
{color: #000 !important;}
#nav .notification a b, .notification a b
{box-shadow: 0 1px #600;
background: #f7590a;
text-shadow: 0 1px #900;
color: #fff;
border-radius: 3px;
text-align: center;
font-size: 11px}
.nav > ul li a
{font-size: 11px;
color: #9da8a8}
.nav li.active a
{color:#c3cece;
background-color: #324048;
border-color:#3cae8e}
.diggbox span:first-child, .rdiggbox
{color:#b7d9e3;
font-size: 12px!important;
background: #425d66;
border-radius: 4px}
.diggbox span.button.submit
{color:#b7d9e3;
background: #425d66!important;
font-size: 10px!important;
border-radius: 2px;
border:0px}
.diggbox a.dropdown-show
{color:#737e86;
font-size: 10px!important}
.diggbox.digout span:first-child
{color:#8aa4ac;
font-size: 12px!important;
background: #2c2f33}
.diggbox.digout span:last-child
{color:#8aa4ac!important;
font-size: 10px!important;
background: #2c2f33!important}
.article h2
{font-size: 14px!important}
a.link, a
{text-decoration: none;
color: #c0c1bc}
body a:hover
{color: #bfccd2 !important}
.article h2 a:visited
{color: #6a6e6e}
.create.tag:nth-of-type(1) > .affect
{color: #46978a!important;
font-weight: bold;
font-style:normal!important}
.article > div p.text
{font-size: 14px!important}
.article > div p.text a
{color: #c0c1bc}
.article > div p.text a:hover
{color: #aecfe5!important;
border-bottom: 0px!important}
.lcontrast .affect
{color: #8d91a1 !important}
.dC.preview.clearfix.article > .m-reset-margin.lcontrast > .elements.row > .affect:nth-of-type(1)
{font-size: 11px}
.nav .info
{color: #707070}
ul.related li > div div.content span
{background:#213734}
.nav {border:0px}
ul.related li > div span.data > span b
{color: #c8bba8}
ul.related li > div div.content
{border:0px!important}
.text p
{color: #c0c1bc!important;
font-size: 14px!important}
.showTagSummary
{color: #d6c352 !important}
.voters-list *,.voters-list
{color: #808f95 !important}
.author p *:first-child
{font-size: 12px!important}
.author i.fa-plus, .c2a7017
{color: #60bb56;
font-size: 12px!important}
.author i.fa-minus, .cbb0000
{color: #ea645a;
font-size: 12px!important}
a.showSpoiler, a.show-more
{color:#151717!important;
background:#56924a}
.media-content.too-long-pic .expand
{font-size: 12px!important;    
color:#cabf56!important;
text-shadow:0px 0px}
.media-content .description a:last-child, .media-content .description
{background:#202225!important;
border-top:0px;
font-size: 10px!important}
.icon-media.play, .icon-media.gif
{color: #fff!important}
.author, .author.hlight
{border-color:#3b4853!important}
.wblock textarea, .wblock form textarea
{color: #c0c1bc!important;
background:#282f37;
font-size: 14px;
border:0px;
border-radius:7px}
.mark-bg, .replyOn .wblock.replyForm
{background:#3b5752!important; border:0}
.button, .button_ddC, .reply fieldset.row.buttons .button a, a.editlenny + a.dropdown-show > .fa.fa-caret-down
{color: #c3c3c3;
background:#283139!important}
.onEnter
{background:#283139!important;border:0px}
button.submit
{color:#a1c0c1;
background:#283139;
border:0}
a.color-2.showProfileSummary
{color:#e52929!important}
label.inline, input[type="text"].create
{color:#9d9693;background: #3b3b3b}
blockquote p.description, .media-header p.description
{border:0}
.button
{color:#768a8e;
background: #80979b;
font-size: 11px;
font-weight: normal;
border: 0px}
#footer h4, #footer a
{color: #6d7175}
#footer a
{color: #696a68}
#footer a.tag:hover
{background-color: #243840; border:0px}
#footer
{background: #191a1b}
#wpladbottom
{display:none!important}
::selection
{background: #fbff8d;
color: #000}
#dyingLinksBox {
display:none!important;
}
}
