css:
@-moz-document domain("downloads.khinsider.com") {
body,
#page {
    background: #1e1e1e !important;
    color: #eaeaea;
}
input {
    background-color: #333232 !important;
    border: #5f5f5f solid 1px !important;
    color: white !important;
}
p,
a {
    color: #cfcbcb;
}
b,
font {
    color: #2798d1;
}
h1,
h2 {
    color: #0af !important;
}
h3 {
    color: #2798d1 !important;
}
#faux {
    background: none !important;
}
#lmenu,
#rmenu .moduletable {
    background-color: #242424 !important;
    height: 1680px !important;
}
#lmenu,
#rmenu a {
    color: white !important;
    text-align: left !important;
}
#alpha .moduletable h3,
#rmenu .moduletable h3 {
    background: #0000;
    color: #c3c3c3 !important;
}
.moduletable td {
    background: none !important;
}
#delta {
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#000000+0,000000+100&0+0,0.65+100 */
    background: -moz-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.65) 100%) !important;
    /* FF3.6-15 */
    background: -webkit-linear-gradient(top, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.65) 100%) !important;
    /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.65) 100%) !important;
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}
#songlist th {
    background-color: #444 !important;
}
#songlist tr:hover,
#top40 tr:hover {
    background-color: #444;
}
.audioplayer {
    background: #313131 !important
}
.audioplayerPlayPause:hover,
.audioplayerPlayPause:focus,
.audioplayerVolume:hover,
.audioplayerVolume:focus,
.audioplayerVolumeBar {
    background: #242424;
}
.audioplayerVolumeBarLevel {
    background: -moz-linear-gradient( left, #959595, #aaa);
}
.audioplayerBarPlayed {
    background: #b7b7b7 !important;
}
.clickable-row a {
    color: white !important;
}
span.pagenav:last-child {
    background: #2798d1;
}
}
