@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("clubnseries.com") {

/*Colores*/
.page {background:black url(http://i43.tinypic.com/zls0pe.jpg)repeat-X !important}
table.page {background:none !important}
a{color:#FFCC66 !important}
a:hover{color:#FF9900 !important}
a:visited{color:#FF9900 !important}
.alt1,.alt2,.alt1pb,.alt2pb,.pbbox,.alt1pbb,.alt2pbb,#collapseobj_module_28,.header,.footer {color:white !important; background:#3a3a3a url(http://i42.tinypic.com/2570yet.jpg) repeat-x    !important}
.tcat,.topbar,.tcat_hp,.tc_right>table>tbody>tr>td {background:url(http://i43.tinypic.com/zls0pe.jpg)repeat-X !important;color:white !important; border:none !important}
.thead,.navigation,#navbar_forumjump {background:url(http://i40.tinypic.com/ra9gjr.jpg) repeat-X bottom !important; color:white !important}
.tborder {border-bottom: solid white 2px !important;border-left: solid white 2px !important;border-right: solid white 2px !important}
.tborder,table.page>tbody>tr>td>div{border:none !important;padding-bottom:0px !important}

table.tborder>tbody>tr>td{color:white !important; background:#3a3a3a url(http://i42.tinypic.com/2570yet.jpg) repeat-x    !important}

.header>table>tbody>tr>td{ padding-left: 20px !important }
.headerright{background: url(http://i43.tinypic.com/10pdhc5.jpg)no-repeat !important}



/*Transparencias*/
.page>div>div,.welcomeleft,.navitem,#navbar_forumjump,.tcat,.header>table>tbody>tr>td>a>img{display:none !important;height:0px !important}
.headerright>table>tbody>tr>td>a{display:none !important}

/*Publicidad*/
#collapseobj_module_19,#collapseobj_module_29 {display:none !important;height:0px !important}

/*Reestructuracion*/
.topbar {height:0px !important; margin-top:0px !important}
.welcomeright {margin-top: -9px !important}
#usercptools,#navbar_forumjump {height:40px !important}
#vB_Editor_001_iframe {display:block !important}
.navigation {margin-top:-40px !important}
div#vba_news4>div>table.tborder>thead>tr>td.tcat {display:block !important; height:20px !important; width:99% !important }
.header {height:80px !important}
.headerright{padding-left:55% !important; padding-top:5px !important}
.headerright>table>tbody>tr>td{width:73% !important}

}
