css:
@-moz-document url-prefix("https://plus.googleapis.com"), domain("www.youtube.com"), regexp("https://apis.google.com/.*/hovercard.*"), regexp("https://apis.google.com/.*/widget/render?.*&first_party_property=YOUTUBE.*"), regexp("https://plus.google.com/.*/widget/render/comments?.*"), regexp("https://plus.google.com/.*/notifications/frame?.*") {
:root {
    --dgyt-bg-color-dark: rgb(15, 15, 15);
    --dgyt-bg-color-medium: rgb(33, 33, 33);
    --dgyt-cell-bg-color: rgba(0, 0, 0, 0.2);
    --dgyt-menu-bg-color: rgba(21, 21, 21, 0.8);
    --dgyt-button-color: rgba(255, 255, 255, 0.1);
    --dgyt-button-color-hover: rgba(255, 255, 255, 0.22);
    --dgyt-border-color: rgba(0, 0, 0, 0.2);
    --dgyt-text-main: rgb(192, 192, 192);
    --dgyt-text-secondary: rgb(142, 142, 142);
    --dgyt-text-highlight: rgb(255, 255, 255);
    --dgyt-color-red: rgb(204, 24, 30);
    --dgyt-color-blue: rgb(22, 122, 198);
    --dgyt-color-yellow: rgb(245, 213, 98);
    --dgyt-color-orange: rgb(255, 85, 0);
    --dgyt-color-purple: rgb(156, 39, 176);
    --dgyt-color-green: rgb(76, 175, 80)
}
.yt-uix-button,
.yt-uix-button:hover,
.guide-item,
.guide-item:hover {
    transition: background-color 0.3s!important
}
/* scrubber button */
.html5-scrubber-button:hover, .ytp-scrubber-button:hover, .ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
   background: #128ee9 !important;
}
/* progress bar */
.html5-play-progress, .ytp-play-progress {
   background: #128ee9 !important; 
}
.ytp-volume-slider-track, .ytp-volume-slider-handle:before {
   background: #128ee9 !important;
   z-index: -117;
}
.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {
   background-color: #128ee9 !important;
}
.ytp-swatch-color {
   color: #128ee9 !important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
   background: #128ee9 !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
   fill: #128ee9 !important;
}
.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg, .ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg {
   fill: #128ee9 !important;
}
.resume-playback-progress-bar {
   background: #128ee9 !important;
}
.ytp-chrome-controls .ytp-button[aria-pressed]::after {
   background-color: #128ee9 !important;
}
.yt-uix-checkbox-on-off input-extras2-disabled[type="checkbox"]:checked + label {
   background-color: #128ee9 !important;
}
.video-extras-sparkbar-likes-extras2-disabled {
   background-color: #128ee9 !important;
}
.guide-flyout-trigger.on-hover,
.guide-item:hover,
.guide-item:hover .yt-deemphasized-text,
.guide-collapsible-item .yt-uix-expander-head:hover {
    background-color: rgba(255, 255, 255, 0.22)!important;
    color: rgb(255, 255, 255)!important
}
.video-extras-sparkbar-likes {
    background-color: rgb(18, 142, 233)!important;
}
::-moz-selection {
    background-color: rgb(22, 122, 198)!important;
    color: rgb(255, 255, 255)!important
}
::selection {
    background-color: rgb(22, 122, 198)!important;
    color: rgb(255, 255, 255)!important
}
body {
    color: rgb(192, 192, 192)!important
}
#body-container {
    background: rgb(15, 15, 15)!important
}
a,
a:hover {
    color: rgb(22, 122, 198)
}
h1,
h2,
h3,
h4,
.playlist-videos-list .yt-ui-ellipsis,
.yt-ui-ellipsis,
.yt-lockup,
.branded-page-module-title,
.branded-page-module-title a,
.yt-thumb {
    background-color: transparent!important
}
#yt-masthead #logo-container .logo,
#masthead-logo img,
.footer-logo img,
