}
body.static-yt-logo #yt-masthead .doodle-removed #logo {
    background-position: 0!important
}
.yt-lockup-dismissable .watched-badge {
    content: " \1F441"!important;
    color: rgb(255, 255, 255)!important;
    opacity: 0.6!important;
    padding: 0!important;
    font-size: 2em!important;
    background-color: transparent!important;
    height: 100%!important;
    width: 100%!important;
    text-align: center!important;
    line-height: 100px!important;
    text-shadow: 0 0 5px rgb(15, 15, 15)!important;
    top: 0!important;
    left: 0!important;
    transition: opacity 0.24s ease-in-out!important
}
.yt-lockup-dismissable .watched-badge:hover {
    opacity: 0!important;
    transition: opacity 0.24s ease-in-out!important
}
.yt-lockup-dismissable .watched .video-thumb {
    -webkit-filter: blur(2px)!important;
    filter: blur(2px)!important;
    transition: 0.48s!important
}
.yt-lockup-dismissable .watched:hover .video-thumb {
    -webkit-filter: blur(0)!important;
    filter: blur(0)!important;
    transition: 0.48s!important
}
.yt-card {
    background-color: rgb(33, 33, 33)!important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2)!important
}
.yt-uix-button-text,
.branded-page-module-title,
.branded-page-module-title-text,
.branded-page-module-title a,
.view-count,
.yt-dialog-fg-content,
.yt-uix-overlay-fg-content,
.yt-dialog-fg-content .yt-dialog-title,
.yt-uix-overlay-fg-content .yt-dialog-title {
    color: rgb(192, 192, 192)!important
}
.yt-dialog-fg,
.yt-uix-overlay-fg {
    border: none!important
}
.yt-uix-button-default,
.yt-uix-button-default[disabled],
.yt-uix-button-default[disabled]:hover,
.yt-uix-button-default[disabled]:active,
.yt-uix-button-default[disabled]:focus {
    border-color: rgba(255, 255, 255, 0.1)!important;
    background-color: rgba(255, 255, 255, 0.1)!important;
    color: rgb(192, 192, 192)!important
}
.yt-uix-button-default:hover,
.yt-uix-button-text:hover {
    background-color: rgba(255, 255, 255, 0.22)!important;
    box-shadow: 0 1px 0 rgba(0, 0, 0, 0.2)!important
}
#yt-masthead-container,
.feed-list,
.feed-item-container {
    background-color: rgb(33, 33, 33)!important;
    border: rgba(21, 21, 21, 0.8)!important
}
.masthead-search-terms-border {
    box-shadow: none!important
}
.gsst_a {
    position: relative!important;
    right: 32px!important
}
.gsfi {
    height: 32px!important;
    position: absolute!important
}
@-webkit-keyframes scaleout {
    0% {
        -webkit-transform: scale(0.0)
    }
    100% {
        -webkit-transform: scale(1.0);
        opacity: 0
    }
}
@keyframes scaleout {
    0% {
        transform: scale(0.0);
        -webkit-transform: scale(0.0)
    }
    100% {
        transform: scale(1.0);
        -webkit-transform: scale(1.0);
        opacity: 0
    }
}
.yt-spinner-img {
    width: 20px;
    height: 20px;
    margin: 0 auto;
    background-image: none!important;
    background-color: rgb(204, 24, 30)!important;
    border-radius: 100%!important;
    -webkit-animation: scaleout 1.0s infinite ease-in-out;
    animation: scaleout 1.0s infinite ease-in-out
}
#appbar-settings-button .yt-uix-button-arrow {
    border-top-color: rgb(15, 15, 15)!important
}
#yt-masthead-user-displayname,
#yt-masthead-notifications-title {
    color: rgb(142, 142, 142)!important
}
#yt-masthead-user #sb-button-notify {
    width: 26px!important;
    background-color: transparent!important;
    margin-right: 10px!important
}
#yt-masthead-user .sb-notif-on .yt-uix-button-content {
    border-color: transparent!important
}
#guide-container,
#appbar-guide-menu,
.guide-toplevel,
#guide,
#appbar-guide-button-container,
#watch-response {
    background-color: rgb(33, 33, 33)!important
}
.branded-page-related-channels-item .yt-close {
    z-index: 2!important;
    cursor: pointer!important
}
.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover,
.guide-item.guide-item-selected .yt-deemphasized-text,
.guide-item.guide-item-selected:hover .yt-deemphasized-text {
    background-color: rgb(204, 24, 30)!important
}
.guide-flyout {
    background-color: rgb(33, 33, 33)!important;
    border-color: rgb(33, 33, 33)!important
}
.guide-flyout-container .guide-view-more {
    cursor: pointer
}
li.guide-section h3 a {
    color: rgb(142, 142, 142)!important
}
#masthead-appbar {
    background-color: rgb(33, 33, 33)!important;
    border: rgba(0, 0, 0, 0.2)!important
}
#masthead-expanded-container {
    background-color: rgb(33, 33, 33)!important;
    border-bottom-color: rgba(0, 0, 0, 0.2)!important
}
#masthead-search label,
#masthead-search-terms,
.masthead-search-terms-border {
    height: 34px;
    top: -2px!important;
    border: none!important;
    background-color: rgb(33, 33, 33)!important;
    margin-bottom: 0!important;
    border-radius: 3px!important
}
#masthead-search-term {
    background: rgba(255, 255, 255, 0.1)!important;
    border: 1px solid rgb(33, 33, 33)!important;
    box-shadow: none!important;
    color: rgb(255, 255, 255)!important;
    border-radius: 2px!important;
    transition: background 0.5s!important
}
#masthead-search-term:hover,
#masthead-search-term:focus {
    background: rgba(255, 255, 255, 0.22)!important;
    transition: background 0.5s!important
}
#masthead-search-terms input {
    padding-left: 10px!important
}
#search-btn {
    background-color: transparent!important;
    width: 50px!important;
    position: relative!important;
    left: -53px!important;
    bottom: 2px!important;
    border: none!important;
    margin-top: 2px!important;
    margin-left: 3px!important;
    border-radius: 2px!important;
    z-index: 1!important;
    box-shadow: none!important
}
#yt-masthead-user .yt-uix-button-icon-bell,
#upload-btn.yt-uix-button-has-icon {
    -webkit-filter: invert(1)!important;
    filter: invert(1)!important
}
#yt-masthead-user #yt-masthead-notifications-button {
    background-color: transparent!important
}
#yt-masthead-user #yt-masthead-notifications-button:hover {
    box-shadow: none!important
}
#appbar-settings-button {
    border-left: 1px solid rgb(33, 33, 33)!important
}
.yt-uix-clickcard-card-reverse .yt-uix-card-body-arrow-vertical,
.yt-uix-clickcard-card-reverse .yt-uix-card-border-arrow-vertical {
    border-bottom-color: rgb(33, 33, 33)!important
}
.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-picker-header,
.yt-uix-clickcard-card.yt-masthead-multilogin .yt-masthead-multilogin-users-header {
    background-color: rgb(33, 33, 33)!important
}
.yt-uix-hovercard-card-body,
.yt-masthead-account-picker.yt-uix-clickcard-card-content,
.yt-uix-clickcard-card-border,
.yt-uix-hovercard-card-border {
    background-color: rgb(33, 33, 33)!important;
    color: rgb(192, 192, 192)!important;
    border-color: transparent!important
}
.yt-masthead-account-picker-option {
    background-color: rgba(0, 0, 0, 0.2)!important
}
.yt-masthead-account-picker-option .yt-masthead-picker-name {
    color: rgb(192, 192, 192)!important
}
.yt-uix-button-icon-icon-account-settings {
    -webkit-filter: invert(1)!important;
    filter: invert(1)!important
}
.yt-masthead-account-picker .yt-masthead-picker-button {
    background-color: rgba(255, 255, 255, 0.22)!important
}
.yt-masthead-picker-footer {
    border-top: 1px solid rgba(0, 0, 0, 0.2)!important
}
.yt-uix-card-body-arrow-vertical {
    border-top-color: rgb(33, 33, 33)!important
}
.yt-uix-card-border-arrow-vertical {
    border-top-color: rgba(0, 0, 0, 0.2)!important
}
.yt-masthead-picker-footer {
    background-color: rgb(33, 33, 33)!important
}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu,
.yt-horizontal-rule {
    border-color: transparent!important
}
.guide-item,
.yt-uix-redirect-link {
    color: rgb(192, 192, 192)!important
}
.yt-uix-redirect-link:hover,
.multirow-shelf .yt-uix-expander-head {
    color: rgb(22, 122, 198)!important
}
.guide-section-separator {
    border: none!important
}
.guide-item.guide-item-selected,
.guide-item:hover {
    color: rgb(255, 255, 255)!important
}
.yt-uix-button-default,
.yt-uix-button-shelf-slider-pager,
.yt-uix-form-input-select,
.yt-uix-form-input-checkbox-element,
.yt-uix-button-subscribe-unbranded {
    text-shadow: none!important;
    background-color: rgba(255, 255, 255, 0.1)!important;
    border: 1px solid transparent!important;
    color: rgb(192, 192, 192)!important
}
.yt-uix-button-subscribe-unbranded:hover {
    background-color: rgba(255, 255, 255, 0.22)!important
}
.yt-uix-shelfslider-prev {
    border-radius: 3px 0 0 3px!important
}
.yt-uix-shelfslider-next {
    border-radius: 0 3px 3px 0!important
}
.site-center-aligned .yt-card {
    background-color: rgb(33, 33, 33)!important
}
.yt-ui-menu-content {
    background-color: rgba(21, 21, 21, 0.8)!important;
    border-color: rgba(21, 21, 21, 0.8)!important
}
.yt-uix-button-menu {
    background-color: rgba(21, 21, 21, 0.8)!important;
    color: rgb(192, 192, 192)!important;
    border: 1px solid transparent!important
}
.yt-uix-button-menu-item {
    color: rgb(192, 192, 192)!important
}
.yt-ui-menu-item {
    color: rgb(192, 192, 192)!important;
    transition: ease-in-out color 0.3s!important
}
.yt-ui-menu-item:hover,
.yt-uix-menu-trigger-selected .yt-ui-menu-item {
    background-color: transparent!important;
    color: rgb(192, 192, 192)!important;
    transition: ease-in-out color 0.15s!important
}
.yt-uix-button-opacity {
    transition: ease-in-out opacity 0.3s!important
}
.yt-uix-button-opacity:hover {
    transition: ease-in-out opacity 0.15s!important
}
.yt-uix-button-arrow {
    border-top-color: rgb(192, 192, 192)!important
}
.yt-uix-button-subscribed-unbranded {
    background-color: rgba(255, 255, 255, 0.1)!important;
    border-color: transparent!important;
    color: rgb(15, 15, 15)!important;
    opacity: 0.6!important
}
.yt-uix-button-subscribed-unbranded:before {
    content: "\2713"!important;
    padding-left: 2px!important;
    color: rgb(192, 192, 192)!important
}
a.yt-uix-button.yt-uix-button-epic-nav-item {
    color: rgb(142, 142, 142)!important;
    border-bottom: solid 3px transparent!important
}
.epic-nav-item-heading {
    color: rgb(192, 192, 192)!important
}
a.yt-uix-button-epic-nav-item:hover:not(.secondary-nav) {
    color: rgb(192, 192, 192)!important;
    border-bottom: 3px solid rgba(255, 255, 255, 0.1)!important
}
a.yt-uix-button-epic-nav-item.partially-selected:hover,
