@-moz-document domain(azurlane.koumakan.jp) {

body {
	background-color: #000;
}
div#mw-head {
	position: absolute;
	top: 0;
	right: 0;
	width: 100%;
	background-color: black;
}
div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
	color: #fff;
	text-decoration: none;
	background-color: #2f2f2f;
}
div#mw-panel div.portal h3 {
	font-size: 0.75em;
	color: #9c9b9b;
	font-weight: normal;
	margin: 0.5em 0 0 0.66666667em;
	padding: 0.25em 0;
	cursor: default;
	border: 0;
}
.azl_box.green .azl_box_body {
	background: #1e451e;
}
.mw-body h1, .mw-body-content h1, .mw-body-content h2 {
	font-family: 'Arial';
	line-height: 1.3;
	margin-bottom: 0.25em;
	padding: 0;
	color: #aba6a6;
}
.mw-body, .parsoid-body {
	padding: 1em;
	background-color: #000;
	color: #717171;
	direction: ltr;
}
.azl_box.yellow .azl_box_head {
	background: #121212 !important;
	color: #6e6969 !important;
}
.azl_box.yellow .azl_box_sub {
	background: #000 !important;
}
v.vectorTabs span > a {
	float: left;
	display: block;
	background-color: black !important;
}
div.vectorTabs span {
	display: inline-block;
	background-image: none !important;
	background-position: bottom right;
	background-repeat: no-repeat;
	height: 100%;
}
div.vectorTabs ul li {
	float: left;
	line-height: 1.125em;
	display: inline-block;
	height: 100%;
	margin: 0;
	padding: 0;
	background-color: #000;
	background-image: none;
	background-position: bottom left;
	background-repeat: repeat-x;
	white-space: nowrap;
}
div.vectorTabs ul {
	float: left;
	height: 100%;
	list-style-type: none;
	list-style-image: none;
	margin: 0;
	padding: 0;
	background-image: none !important;
	background-position: right bottom;
	background-repeat: no-repeat;
}
div.vectorTabs li.selected {
	background-image: none !important;
}
div.vectorTabs {
	float: left;
	height: 2.5em;
	background-image: none;
	background-position: bottom left;
	background-repeat: no-repeat;
	padding-left: 1px;
}
a:hover, a:focus {
	text-decoration: underline;
	background-color: #171616 !important;
}
.azl_box.green .azl_box_body {
	background: #182d18 !important;
	color: #746a6a !important;
}
.azl_news .azl_news_date {
	float: left;
	width: 50px;
	height: 80px;
	position: relative;
	z-index: 3;
	margin: 0px 10px 0px 0px;
	text-align: center;
	font-weight: bold;
	padding: 18px 0px;
	background: #121212 !important;
	color: #6b6b6b !important;
}
.azl_box.pink .azl_box_head {
	background: #121212 !important;
	color: #6e6969 !important;
}
.azl_box.pink .azl_box_sub {
	background: #000 !important;
}
.azl_box .azl_box_head {
	height: 30px;
	position: relative;
	overflow: none;
	border-radius: 5px 5px 0px 0px;
	margin: 0px 0px 1px 0px;
	color: #6e6969 !important;
	background: #121212 !important;
}
.azl_box .azl_box_body {
	min-height: 20px;
	position: relative;
	border-radius: 0px 0px 8px 8px;
	overflow: hidden;
	background: #112132b3 !important;
}
div#simpleSearch {
	display: block;
	width: 12.6em;
	width: 20vw;
	min-width: 5em;
	max-width: 20em;
	padding-right: 1.4em;
	height: 1.4em;
	margin-top: 0.65em;
	position: relative;
	min-height: 1px;
	border: solid 1px #aaa;
	color: #000;
	background-color: #000;
	background-image: none;
	background-position: top left;
	background-repeat: repeat-x;
}
.sitedir-ltr textarea, .sitedir-ltr input {
	direction: ltr;
}


table.wikitable {
	color: #8a8a8a;
	border-collapse: collapse;
}
table.wikitable > tr > th, table.wikitable > * > tr > th {
	background-color: #000;
	text-align: center;
	color: #54545a;
}
table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td {
	border: 1px solid #2a2a2a;
	padding: 0.2em 0.4em;
	background-color: #000 !important;
	color: #6b6b6b;
}
pre, .mw-code {
	color: #8d8282;
	background-color: #f8f9fa;
	border: 1px solid #eaecf0;
	padding: 1em;
	white-space: pre-wrap;
}
pre, code, tt, kbd, samp, .mw-code {
	font-family: 'Arial';
}
.catlinks {
	border: 1px solid #2a2a2a;
	background-color: #000;
	padding: 5px;
	margin-top: 1em;
	clear: both;
}
#mw-page-base {
	height: 5em;
	background-position: bottom left;
	background-repeat: repeat-x;
	background-image: none;
	background-color: #f6f6f6;
	background-image: -webkit-gradient(linear,left top,left bottom,color-stop(50%,#ffffff),color-stop(100%,#f6f6f6));
	background-image: -webkit-linear-gradient(top,#ffffff 50%,#f6f6f6 100%);
	background-image: linear-gradient(#000 50%,#000 100%);
	background-color: #000;
}
.sitedir-ltr textarea, .sitedir-ltr input {
	direction: ltr;
	background-color: #777272;
	border-color: #000;
}
td.mw-input {
	text-align: left;
	color: #9e9e9e;
}
.sitedir-ltr textarea, .sitedir-ltr input {
	direction: ltr;
	background-color: #1a1a1a;
	border-color: #000;
}
.mw-ui-input {
	background-color: #fff;
	color: #909090;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	display: block;
	width: 100%;
	border: 1px solid #a2a9b1;
	border-radius: 2px;
	padding: 0.625em 0.625em 0.546875em;
	box-shadow: inset 0 0 0 0.1em #fff;
	font-family: inherit;
	font-size: inherit;
	line-height: 1.172em;
	vertical-align: middle;
}
.mw-ui-vform .error {
	color: #dd3333;
	border: 1px solid #5c3535;
	background-color: #2a1818;
}
#toc, .toc, .mw-warning, .toccolours {
	border: 1px solid #2a5483;
	background-color: #000;
	padding: 5px;
	font-size: 95%;
}
div.thumbinner {
	border: 1px solid #707174;
	padding: 3px;
	background-color: #000;
	font-size: 94%;
	text-align: center;
	overflow: hidden;
	color: #717177;
}
ul.tabbernav li a {
	padding: 3px .5em;
	margin-left: 3px;
	border: 1px solid #262626;
	border-bottom: none;
	background: #000;
	text-decoration: none;
	white-space: pre;
}
ul.tabbernav li.tabberactive a {
	background-color: #272727;
	border-bottom: 1px solid #9B9B9B;
}
a:hover, a:focus {
	text-decoration: underline;
	background-color: #323232 !important;
}
ul.tabbernav li a:hover {
	color: #9f9f9f;
	background: #000;
	border-color: #000;
}
.sitedir-ltr textarea, .sitedir-ltr input {
	direction: ltr;
	background-color: #1a1a1a;
	border-color: #000;
	color: #9f9f9f;
}
.mw-search-profile-tabs {
	background-color: #000;
	margin-top: 1em;
	border: 1px solid #060606;
	border-radius: 2px;
}
#pagehistory li.selected {
	background-color: #000;
	border: 1px dashed #666;
}
#pagehistory li {
	border: 1px solid #323232;
}
.diff-context {
	background: #000;
	border-color: #242424;
	color: #a4a4a4;
}
.diff-deletedline .diffchange {
	background: #444309;
}
.diff-deletedline .diffchange {
	background: #634703;
}
.sitedir-ltr textarea, .sitedir-ltr input {
	direction: ltr;
	background-color: #1a1a1a !important;
	border-color: #000;
	color: #9f9f9f;
}
code {
	color: #aaa;
	background-color: #000;
	border: 1px solid #353535;
	border-radius: 2px;
	padding: 1px 4px;
}
.client-js .mw-ui-checkbox:not(#noop) [type="checkbox"]:enabled:checked + label::before {
	background-color: #000;
	border-color: #3366cc;
}
table.jquery-tablesorter th.headerSort {
	cursor: pointer;
	background-repeat: no-repeat;
	background-position: center right;
	padding-right: 21px;
	background-color: #1a1a1a !important;
}
table.wikitable {
	background-color: #000;
	color: #222;
	margin: 1em 0;
	border: 1px solid #a2a9b1;
	border-collapse: collapse;
}
pre, .mw-code {
	color: #8a8a8a;
	background-color: #151515;
	border: 1px solid #9f9f9f;
	padding: 1em;
	white-space: pre-wrap;
}
}
