css:
@-moz-document url-prefix("http://scoobysworkshop.com") {

p {
    font-size:1.1em;
    letter-spacing: -0.02em;
    line-height: 1.5em;
    font-family:Roboto;
}
.hupso-share-buttons {
    padding-top:0;
}
.entry-header > * {
}
.entry-title {
    padding-bottom:10px;
    margin:0;
    padding:0;
}
 .entry-content img {
    border-radius:6px;
    margin:3px;
}
.entry-content a:active {
    color: blue;
}
.entry-content a:visited  {
    color: purple;
}
.entry-content a:hover {
    color: teal;
    text-decoration: underline;
}

div .entry-content table {
  border-spacing: 1;
  border-collapse: collapse;
  border-radius: 10px;
  overflow: hidden;
 
 
  position: relative;
}

div .entry-content table td, table th {
  padding-left: 8px;
}
div.entry-content table thead tr {
  height: 60px;
  background: #36304a;
}
div.entry-content table tbody tr {
  height: 47px;
}

.entry-contenttable td, table th {
  text-align: left;
}
.entry-contenttable td.l, table th.l {
  text-align: right;
}
.entry-contenttable td.c, table th.c {
  text-align: center;
}
.entry-contenttable td.r, table th.r {
  text-align: center;
}
.entry-content tbody tr {
  background-color: #fAFAFA
}


.entry-content tbody tr {
  font-size: 15px;
  color: #808080;
  line-height: 1.2;
}

div.entry-content tbody tr:hover {
  color: #555555;
  background-color: #f5f5f5;

}
.entry-content tr:first-child
{
    background:#5D91BF;
    color:White;
    
}
.entry-content tr:last-child
{
    color:black;
     background:#FAFAFA;
}
div.entry-content tr:last-child:hover
{
    color: black;
    
}
.entry-content ul  { font-family:Georgia, Times, serif; font-size:15px;  }

 .entry-content ul li a { 
              text-decoration:none; 
              padding-right:0.7em;
              
              background-color:#FFFFFF; 
              line-height:30px;
              border-bottom-style:solid;
              border-bottom-width:1px;
              border-bottom-color:#CCCCCC; 
              padding-left:10px;
              cursor:pointer; }

 .entry-contentul li a strong { margin-right:10px; }

.header{
    padding-top:0;
   
}
.header .header-inner
{
    padding-top:0;
}
.header .header_row
{
    line-height:0;
}

.header .logo
{
    padding-top:10px;
  	display:inline;
  	height:10em;
   
}
.header .logo img {
	 float:left;
}
.header .logo span{
     float:left;
	 margin-left:20px;
	 padding-top:20px;
}
.header_row{
   
	float:left ; 
    
}

.header  .iframe-class{
    margin-top:-2px;
    overflow:visible;
    margin-left:-8px;
}
.header  .social-icons{
       clear:both;
  		margin-top:4px;
  	    margin-left:4px;
}
div.entry-content form table
{
    border-radius:4px;
}
#footer-wrapper
{
    padding-top:20px;
    overflow:hidden;
   
}
#footer-wrapper *
{
	padding-bottom:0;
    margin-bottom:0;
    overflow:hidden;
}

#twitter-widget-0{
	border-color:#121212;
}
.copyright-txt
{
	margin-bottom:1.4em;
}
section{
	padding-top:2em;
    padding-bottom:2em;
}
}
