css:
@-moz-document domain("www.duolingo.com") {

/* Scroll bar */
 ::selection {
    background-color: #1493d1
}
::-webkit-scrollbar {
    height: 32px;
    width: 15px
}
::-webkit-scrollbar-thumb {
box-shadow: inset 2px 2px 3px #1caff6;
    background: #1493d1
}
::-webkit-scrollbar-thumb:hover {
box-shadow: inset 2px 2px 11px rgba(0, 0, 0, .0);
    background: #1493d1
}
::-webkit-scrollbar-thumb:active {
box-shadow: inset 2px 2px 3px rgba(0, 0, 0, .0);
    background: #1493d1
}

/* Background */
 body {
    background-color: #f3f3f3;
    background: url(https://a.thumbs.redditmedia.com/n-IwQZ2QVqfhV7Q4.png) no-repeat bottom center #f3f3f3;
    background-attachment: fixed;
}

/* Gray box */
 .box-gray {
    background: white;
    box-shadow: 0 0 25px rgba(0, 0, 0, 0.04);
    border: none
}
.btn-white {
    background: #f3f3f3
}
.btn-outline  {
    background: #1caff6
}

/* REMOVE ADS */
 ._2x-xe, ._1bv61,  .sidebar-ad, .adunit, .sidebar-ad, .remove-ads, ._2CPqs {
    display: none;
    visibility: hidden
}

}
