css:
@-moz-document url-prefix("https://www.insomnia.gr") {

.breadcrumb
{
    padding-top:7px;   
    padding-bottom:0px;

}
#elMobileNav
{
    position:fixed;
    right:0;
    z-index:9999;
    float:right;
}
#masthead{
  position:fixed;
    z-index:9998;
    top:0;
    min-height:0px;
    height:70px;
    overflow:hidden;
}
.banner-728x90-top 
{
    margin-top:70px;
    padding-top:0;
    margin-bottom:0;
    padding-bottom: 0 !important;
    background-color:#F0F1F3;
    border: 0 solid white;
}
#menu
{
    margin-top:-14px !important;
}
#ipsLayout_mainArea >*
{
    padding:0 !important;    
    margin:0 !important;

}
div.ipsPageHeader {
	  display:inline !important;
    float:left !important;
  
    clear:none !important
}
.ipsToolList_horizontal {
    margin:-20px;
}

.ipsToolList_horizontal {
    clear:none ;
    display:block !important;
    float:right;
}
.ipsSpacer_bottom{
margin-bottom:5px;
}
div.ipsBox{
	border:0px solid white;
    background-color:#F0F1F3 !important;
}
ul.ipsToolList {
	margin:0px;
}
.site-footer *{
    max-height:10em;
}
.ipsPhotoPanel 
{
    margin-top:5px;
}
div.ipsPageHeader > div.ipsPromote-followdiv{
	display:block;
}
.ipsType_light p
{
    font-size:0.9em;
    
}
hr.ipsHr
{
    padding:0;
    margin:0;
}
li.cAuthorPane_photo img
{
    border-radius:4px;
}
div.ipsType_richText *{
    padding:0;
    margin:0;
    line-height:1.6;
}
.ipsQuote_citation {
	padding:5px 15px !important;	
   

}


.ipsQuote  *{
	margin:3px !important;	
    padding:10px;
}
div.featured-2{
	padding:0px;
    margin:0px;
}
}
