css:
@-moz-document domain("twitter.com")
{
/* this is just simple style for my practice */

/* update still on proccess */


body {
    background: black;
    background: linear-gradient(to right, rgba(255,0,0,0), rgba(255,67,200,1));
}
.stream-item:not(.no-header-background-module) {
    background-color: rgba(255, 255, 255, 0.4);
}

/* transparent */
.module .flex-module {
    background-color: #ffffff80;
    border: 1px solid #e6ecf0;
    padding: 15px;
    line-height: 16px;
}
.DashboardProfileCard {
    border: 1px solid #e6ecf0;
    background: #ffffff80;
}
.ProfileOnboarding-module {
    background-color: #ffffff80;
    border: 1px solid #e6ecf0;
    border-radius: 5px;
    display: block;
    padding: 15px;
}
.Trends {
    background-color: #ffffff80;
    background-clip: inherit;
    border: 1px solid #e6ecf0;
    line-height: inherit;
    padding: 15px;
}
.home-tweet-box, .LiveVideo-tweetBox, .RetweetDialog-commentBox {
    background-color: #ffffff80;
}
.NotificationsEmptyModule {
    background-color: #ffffff80;
}
.NotificationsHeadingContent {
    background-color: #FF43C8;
    border-bottom: 1px solid #e6ecf0;
}
.DMActivity-body {
    background-color: #FF43C8;
}
#search-query input[type="text"] {
    border: none;
    border-bottom: 1px solid #fff;
    background: transparent;
    outline: none;
    height: 40px;
    color: hotpink;
    font-size: 16px;
}
}
