css:
@-moz-document url-prefix("https://trimps.github.io/") {
.title, .titleDiv {
    font-size: 1.0em;
}
#science, #metal, #wood, #food {
    max-height: 145px;
}
#topRow {
    height: 15vw;
}
.nav-tabs>li>a {
    font-size: 0.8vw;
}
#buyContainer {
    height: calc(99vh - 20vw - 120px);
}
#rightCol {
    height: calc(99vh - 20vw - 5px);
}
#buyHere.buttonSizeTiny div .buyBox .thing {
    font-size: 11px;
}
.autoUpgradeBtn {
    font-size: 0.5vw;
}
#scienceCollectBtn {
    font-size: 1.2vw;
}
#miscColumn span {
    font-size: 1.0vw !important;
}
.playerGather {
    font-size: 1vw;
}
.playerGatherSm {
    height: 32.5%;
}
.message {
    font-size: .8vw;
}
#trimpsTimeToFill {
	margin-top: 2.5%;
}
#upgradesTitleSpan, #jobsTitleSpan, #equipmentTitleDiv, #equipmentTitleSpan {
    display: none !important;
}
#buyTabsUl {
    display: none;
}
#equipmentContainer {
    margin-top: 5px;
}
#queueRow, #noQueue {
    font-size: 1em;
}
#queueContainer {
    height: 67px;
}
.progress div span {
    font-size: 0.8vw;
    margin-top: -2px;
}
.fightBar div span {
   font-size: 1vw !important;
    margin-top: 0px;
}
}
