css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {

body[bgcolor]
{ background: url("http://s5.tinypic.com/142ycl4.jpg") top right no-repeat fixed #000000 !important;}


/* hides ads */
div.logo img{display:none !important;}
iframe{display:none !important;}

/* hr */
hr{display:none !important;}
form hr{display:block !important;}
hr{height:1px !important;border:none !important;background:#999 !important;}

/* rounded corners */
td.replyhl, td.reply{-moz-border-radius:8px !important;}

/* links */
a{text-decoration:none !important;}
a.quotejs:hover, a:hover{font-weight:normal !important;text-decoration:underline !important;}

/* removes ugly borders on page overview [0,1,2...] */
table,td{border:none !important;}
td.rules{display:none !important;}

/* logo */
img + br {display:none !important;}


/* blurry thumbnails fix */
img[md5] {
height: auto !important;
width: auto !important;

}
body {
background: #2C2C2C !important;
color: #eee !important;
}

a {
color: #efefef !important;
}

hr {
display: none !important;
}

#navtop {
width: 99.6% !important;
text-align: center !important;
color: #000000!important;
padding: .4em !important;
top: 0;
z-index: 1;
}

#navtop a:hover {
background: #555 !important;
}

.logo,.logo font b span{
color: #ffffff !important;
}

form {
margin: 1em !important;
opacity: .9
}

.inputtext, textarea {
width: 295px !important;
opacity: .9
}

.rules{
display: none !important;
}

td.postblock {
background: #444 !important;
border: 1px solid #666 !important;
color: #aaa !important;
opacity: .9
}

td.reply {
background-color:#000000 !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #555 !important;
color: #eee !important;
-moz-border-radius:3px !important;
opacity: .8

}

td.replyhl {
background-color:#660000 !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #4B4B4B !important;
color: #eee !important;
-moz-border-radius:3px !important;
opacity: .8
}

.postername, .commentpostername, .omittedposts, .abbr {
color: #fff !important;
}

.filetitle, .replytitle {
color: #999999 !important;
font-size: medium;
}

.doubledash {
visibility: hidden !important;
}





th {
background: #000000 !important;
padding: .2em !important;
font-size: small;
width: 200px !important;
opacity: .5
}

#hd {
display: none;
}

.boxbar {
background: #444 !important;
color: #eee !important;
}

.box-outer {
border: none !important;
background: #333 !important;
color: #eee !important;
}

.box-inner {
background: #333 !important;
color: #eee !important;
}

#filter-button, #option-button {
display: none;
}

#ft{
display: none;
}

.pages {
background: #444 !important;
border: none !important;
}

.pages td, .pages td b {
color: #eee !important;
}

#recent-images li{
background: #333 !important;
}
pre, textarea {
color: white !important;
background: #333333 !important;
font-size: 12pt ! important;
border-left-color: gray !important;
border-right-color: gray !important;
border-top-color: gray !important;
border-bottom-color: gray !important;
opacity: .9
}
input:not([type="button"]):not([type="checkbox"])
:not([type="submit"]):not([type="reset"]),
select {
color: red !important;
background: #333333 !important;
border-left-color: gray !important;
border-right-color: gray !important;
border-top-color: gray !important;
border-bottom-color: gray !important;
opacity: .9
}
.postarea input[type="file"]{
opacity: .5 !important;
}

}
