css:
@-moz-document domain("userstyles.org") {
/*------------ Основа -------------*/
  *:not(.coloris)
  
  {
    color: skyblue !important;
    box-shadow: none !important;
    background-color: transparent !important;
    border-color: rgba(135,206,235,0.1) !important;
  }
    html, #newtab-window, [id="Frame"], body, [id="root"], [lang="en"]
  {
    background-color: rgb(0,0,0) !important;
  }
  #footer
  {
    background-color: rgb(0, 0, 0, .9) !important;
    border-top: solid 1px #2895ae;
  }
/*----------- Навигация -----------*/
  .navigation
  {
    border-right: solid 1px #2895ae;
	  background-color: rgb(0,0,0,.8) !important;
  }
  .type_search_select
  {
    background-color: rgb(0,0,0,.8) !important;
  }
/*-------------- Шапка -------------*/  
  [id="header"]
  {
    background-image: linear-gradient(rgb(20,20,20,.9), rgb(0,0,0,.9)) !important;
    box-shadow: 1px 1px 7px rgb(135,206,235, .3) !important;
    border-radius: 4px !important;
  }
  [id="overlay-info"]
  {
    background-color: rgb(0,0,0,.8) !important;
	border-radius: 0px !important;
  }
  .tooltip .tooltiptext
  {
    background-color: rgb(0,0,0,.8) !important;
  }
/*-------------  -------------*/ 
  .css_button, #install_button_homepage
  {
    background-image: linear-gradient(rgb(20,20,20,.9), rgb(0,0,0,.9)) !important;
  }
/*------------- Андройд -------------*/  
  [class="take_survey"], [class="coming_soon_title"]
  {
    background-color: #87ceeb17 !important;
  }
  .android_button_banner, .overlay_background .overlay_container .overlay_flex_div, .android_button_button .relative_div .x_button, .android_button_button
  {
    background-color: rgb(0,0,0,.8) !important;
  }
  .overlay_background
  {
    background-color: rgb(0,0,0,.6) !important;
  }
  .android_button_button
  {
    border: solid 1px #2895ae;
  }
  .walking .body div, .walking .head, .walking .head div:first-child, .walking .head div:nth-child(2), .walking .body
  {
    background: #2c3c46 !important;
  }
  .walking .head div:nth-child(3) div
  {
    background: #FFF !important;
  }
  .walking .shadow, .walking .shadow div:first-child, .walking .shadow div:last-child
  {
    background: #b2b2b2 !important;
  }
/*-------------- Андройд Лайв -------------*/
  .flags .flag:nth-of-type(1) 
  {
    background: #f39c12 !important;
  }
  .flags .flag:nth-of-type(2) 
  {
    background: #3498db !important;
  }
  .flags .flag:nth-of-type(3) 
  {
    background: #2e3e48 !important;
  }
  .flags .flag:nth-of-type(4) 
  {
    background: #e74c3c !important;
  }
  .flags .flag:nth-of-type(5)
  {
    background: #f39c12 !important;
  }
  .flags .flag:nth-of-type(6)
  {
    background: #3498db !important;
  }
/*------------ Бордер -------------*/
  [class="no-select"], #Head .SiteSearch .InputBox, .Count, .android_button_banner, .type_search_select, .overlay_background .overlay_container .overlay_flex_div, .choose_editor .boxesContainer .box .cta, .comment-content.edit, .comments-wrapper .show-more-wrapper .show-more-button, .comment-content , .us-button, .personal-page .light-blue-button, .see_more, #report-style-wrapper, .login-page .mdl-card, [id="install_stylish_style_button"]
  {
    border: solid 1px rgb(135,206,235,0.1) !important;
  }
  .NotificationLine
  {
    border-top: solid 1px rgb(135,206,235,0.1) !important;
  }
  .overlay_background .overlay_container .overlay_flex_div, #report-style-wrapper
  {
    border-radius: 4px !important;
  }
  .comment-content, .comment-content.edit
  {
    border-radius: 8px !important;
  }
  .right #account, .right #create_new_style, .right .header_button.discord_button
  {
    border-radius: 10px !important;
  }
  #notes_textarea
  {
    border: solid 1px rgb(135,206,235,0.1) !important;
    padding-left: 8px !important;
  } 
/*------------- Фон -----------*/
  .Button, .choose_editor .boxesContainer .box .cta
  {
    background-image: linear-gradient(rgb(20,20,20,.9), rgb(0,0,0,.9)) !important;
  }
  .EmoticonContainer, .MeBox .Flyout, .NotificationLine, .fzKkUK, .choose_editor, #report-style-wrapper, .login-page .mdl-card
  {
    background-color: rgb(0,0,0,.8) !important;
  }
  .popup_overlay_background, #preview_image_div .recommendations.loaded
  {
    background-color: rgb(0,0,0,.5) !important;
  }
/*----------- Иконки -------------*/
  .Sprite16, .overlay_background .overlay_container .close_overlay_button, [src="/ui/images/icons/x_button_grey_background.svg"]
  {
    filter: saturate(0)invert(1);
  }
/*-------------- Свечение текста --------------*/
  h1 a:hover, h2 a:hover, h2 a:hover *, h3 a:hover, h4 a:hover, h5 a:hover, h6 a:hover, button:hover, button:hover *, a:hover, a:hover *, [class*="no-select"]:hover *, [class="css_button"]:hover *, [class="take_survey"]:hover *, .Button:hover, #install_button_homepage:hover *, .android_button_banner .android_button_banner_right .android_button_banner_cta:hover *, .no-select:hover
  {
    color: rgb(255,255,255) !important;
    text-shadow: 0 0 7px rgba(255,255,255,0.5) !important;
    text-decoration: none !important;
  }
    a, #style-info-author-carousel a:hover
  {
    text-decoration: none !important;
  }
  .header_button a
  {
    color: rgb(135,206,235) !important;
  }
  [href*=" "]:hover
  {
    color: rgb(0,191,255) !important;
  }
  .Button
  {
    text-shadow: none;
  }
/*-------------- Фикс положения --------------*/
  .show-more-button
  {
    margin: 10px !important;
  }
  .comment-content .content-wrapper .content textarea
  {
    padding: 8px !important;
  }
}
