css:
@-moz-document url-prefix("https://journey.cloud/") {
/*----------Home page----------*/

#navigation div div.right .button {
    font-size: 16px !important;
    font-family: "Roboto Condensed";
    font-weight: 400;
    border-radius: 10px;
    padding: 5px 5px 5px 5px;
    background-color: #2ca3c1;
    border: 1px solid transparent;
    box-shadow: none;
    margin-right: 25px;
}

#navigation div div.right .button:hover {
    transition: 300ms ease;
    background-color: #2387a0;
    box-shadow: 0px 2px 10px -5px #555;
}

#navigation div div.right .button:focus {
    transition: 200ms ease;
    background-color: #2387a0;
    box-shadow: 0px 6px 10px -5px #555;
}

body[data-controller='home'][data-view='index'] .platforms .wrapper h2 {
    font-family: "Roboto Condensed" !important;
    font-weight: 700;
}

body[data-controller='home'][data-view='index'] #devices .devices p {
    font-family: "Roboto" !important;
    font-weight: 400;
}

body[data-controller='home'][data-view='index'] .platforms .features {
    font-family: "Roboto";
    font-weight: 400;
}
}

@-moz-document url-prefix("https://journey.cloud/") {
/*----------General----------*/

.has-nav {
    background: #EEE;
}

html, body, h1, h2, h3, h4, h5, h6, p, a, div {
    font-family: "Roboto", " Noto Sans CJK TC Bold" !important;
}
}

@-moz-document url-prefix("https://journey.cloud/") {
/*----------Header----------*/

#navigation {
    height: 64px !important;
    background: #35C4E6;
}

#navigation div div.left  {
    content: url("https://i.imgur.com/wm3QbDG.png");
    transform: scale(0.3);
    margin-top: -40px;
    filter: brightness(40%);
}

#navigation>div .right {
        margin: -10px 33px auto;
}

.expand-button {
    background-image: url("https://raw.githubusercontent.com/google/material-design-icons/master/navigation/2x_web/ic_expand_more_white_48dp.png");
    filter: brightness(25%);
    transform: scale(0.75);
    margin-top: 3px;
    margin-left: -5px;
}

.expand-menu {
    top: -20px !important;
    border: 1px solid transparent;
    border-radius: 8px;
    z-index: 100000;
}

.expand-menu li {
    font-weight: 400 !important;
}

.expand-menu li:hover {
    background: #ddd;
    border-radius: 8px;
}
}

@-moz-document url-prefix("https://journey.cloud/") {
/*----------Posts----------*/

/*---Posting---*/

body[data-controller='home'][data-view='me'] ul#new li.post, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] ul#new li.post, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] ul#new li.post {
    box-shadow: 0px 4px 10px #bbb;
    border-radius: 8px;
}

body[data-controller='home'][data-view='me'] ul#new #choose.unfilled, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] ul#new #choose.unfilled, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] ul#new #choose.unfilled {
    background-image: url('https://raw.githubusercontent.com/google/material-design-icons/master/image/2x_web/ic_add_a_photo_white_48dp.png') !important;
}

body[data-controller='home'][data-view='me'] ul#new #write #main-editor-placeholder, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] ul#new #write #main-editor-placeholder, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] ul#new #write #main-editor-placeholder {
    font-family: "Roboto Condensed" !important;
    transition: 1000ms ease;
}

.button {
    font-size: 0px;
    box-shadow: 0px 6px 30px #ddd;
    border-radius: 100px;
    padding: 5px 5px 5px 5px;
    transition: 300ms ease;
    background-color: #35C4E6;
    border: 1px solid #35C4E6;
}

.button:hover {
    box-shadow: 0px 12px 30px #ddd;
    background-color: #32d0f5;
}
.button:focus {
    box-shadow: 0px 12px 30px #ddd;
    background-color: #32d0f5;
    filter: brightness(105%);
}

.button:after {
    content: url('https://raw.githubusercontent.com/google/material-design-icons/master/action/2x_web/ic_done_white_16dp.png');
    display: inline-block;
    transform: scale(0.7);
}

/*---Posts---*/

body[data-controller='home'][data-view='me'] ul#content li.post, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] ul#content li.post, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] ul#content li.post {
    background: #fff;
    box-shadow: 0px 2px 5px #bbb;
    border-radius: 8px;
}

.info-top, .text-wrapper {
    margin-bottom: 0px !important;
}

body[data-controller='home'][data-view='me'] ul#content li.post>div, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] ul#content li.post>div, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] ul#content li.post>div {
    margin-bottom: 20px;
}

body[data-controller='home'][data-view='me'] #load_more_button, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] #load_more_button, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] #load_more_button {
    border-radius: 100px;
    width: 55px;
}

.post.btn-group {
    font-size: 0 !important;
    border-radius: 100px;
}

.post.btn-group:after {
    content: url("https://raw.githubusercontent.com/google/material-design-icons/master/navigation/2x_web/ic_refresh_white_18dp.png");
    display: inline-block;
    margin-top: 10px;
}
    
.address:after, .temp:after {
    content: "ã€€/ ";
}

/*---Photos---*/

ul#content li.post .photo-wrapper .photo, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] ul#content li.post .photo-wrapper .photo {
    margin-top: 20px;
    border-radius: 8px;
    transition: 200ms ease;
}

ul#content li.post .photo-wrapper .photo:hover {
    transform: scale(1.05);
}

body[data-controller='home'][data-view='me'] #superbox, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] #superbox, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] #superbox {
    background-color: #272727DE;
}

body[data-controller='home'][data-view='me'] ul#content li.post .photo-wrapper .photo .loading, body[data-controller='home'][data-view='fixer'][data-action='corrupt'] ul#content li.post .photo-wrapper .photo .loading, body[data-controller='home'][data-view='fixer'][data-action='duplicate'] ul#content li.post .photo-wrapper .photo .loading {
    background: url('https://raw.githubusercontent.com/google/material-design-icons/master/navigation/2x_web/ic_refresh_white_48dp.png') no-repeat;
    background-position: center center;
}
}

@-moz-document url-prefix("https://journey.cloud/") {
/*----------Footer----------*/

footer {
    margin-top: 0px;
    border-top: 50px solid #EEE;
}

footer #sitemap {
    font-weight: 400;
    letter-spacing: normal;
}

/*---Toast---*/

#noty_bottomLeft_layout_container li {
    background: #323232 !important;
    border-radius: 4px;
    width: 400px !important;
}

.noty_message span {
    font-size: 14px;
    line-height: 24px;
    text-shadow: none !important;
    padding-left: 12px;
}

.noty_message {
    text-shadow: none !important;
}
}

@-moz-document url("https://journey.cloud/settings") {
/*----------Settings----------*/

/*---Font---*/

span.switch_label {
    font-family: "Roboto Condensed";
    font-weight: 700;
}

/*---Toggle---*/

.binary-switch .binary-switch-button {
    background: #35C4E6;
    transform: scale(1.7);
}

.binary-switch input[type="checkbox"]:checked:not(.muted) ~ .binary-switch-track {
    background: #35C4E680
}

.binary-switch .binary-switch-track {
    background: #00000026
}

/*---Button---*/

#content > li:nth-child(3) > div > a, #content > li:nth-child(6) > div > a {
    padding-top: 11px;
}

/*---Footer---*/

footer {
    margin-top: -30px;
}
}
