css:
@-moz-document url-prefix("https://flathub.org/") {
body,
.drawer-container,
.mat-drawer-side {
	background-color: #222 !important;
    color: #d6d6d6 !important;
}
.docs-component-viewer-nav {
	background-color: rgba(255,255,255,.1) !important;
}
.mat-drawer-side a {
    color: #d6d6d6 !important;
}
.docs-component-viewer-nav-content hr {
    border-top: 1px solid rgba(255,255,255,.1) !important;
}
.mat-grid-tile > figure {
	background: rgba(0,0,0,.8) !important;
}
gallery-main > .g-container {
	background: rgba(0,0,0,.3) !important;
}
.mat-raised-button {
	background-color: #4a86cf !important;
}
.app-info-item-value-text[_ngcontent-c20] {
    color: #d6d6d6 !important;
}
.mat-raised-button > span,
.app-info-item-title[_ngcontent-c11],
.app-info-item-value-text[_ngcontent-c11],
.app-info-item-value-text[_ngcontent-c18],
.app-info-item-title[_ngcontent-c18] { 
	color: #d6d6d6 !important;
}
div.pre[_ngcontent-c15],
div.pre[_ngcontent-c22] {
	background-color: #464646 !important;
	color: #d6d6d6 !important;
}
}
