css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imageshack.us") {
   .table_decoration tr td[valign="top"]:first-child, #ymaddiv, img#headlogo, img[src*="rss_icon.jpg"], #bigfootaddiv, #yieldaddiv, #butan_wrap label, #adIframe, #retwit, #loginbox, a[href*="recovery"], body > table td table td > form, #regtags {
      display: none !important;
   }
   table[width="755"], table[width="770"], table[width="760"], table[width="755"] tr, table[width="760"] tr {
      width: 400px !important;
   }
   td[align="right"] table td[valign="top"] {
      width: 210px !important;
   }
   td[align="right"] table td[valign="top"] form {
      width: 180px !important;
   }
   .search {
      width: 128px !important;
   }
   body > table {
      background: url(http://imageshack.us/images/greylogo.png) no-repeat center 0 ;
   }

   a[title="ImageShack homepage"] {
      display: block !important;
      width: 150px !important;
      height: 80px !important;
/*      background-image: url(http://img210.imageshack.us/img210/2902/logoen1.png) !important; */
   }
   #container {
      width: 390px !important;
   }
   input[name="user"], input[name="email1"], input[name="email2"] {
      width: 150px !important;
   }
   input[onClick='highlight(this);'], textarea[onClick="highlight(this)"], input[onClick="highlight(this)"] {
      width: 250px !important;
   }
   td[width="20"] + td {
      display: none !important;
   }
   #container div table td input {
      font-size: 12px !important;
      width: 250px !important;
      height: 15px !important;
   }
   #container div table td, #container div table td a {
      font-size: 12px !important;
   }
   #container iframe {
      display: none !important;
   }
   #container input[value*="http"], #container textarea {
      width: 250px !important;
   }
   #container input[value*="Thanks"], a[title="QuickPost"], ins {
      display: none !important;
   }
   #upform, #upform table {
      text-align: center !important;
   }
   body > table table div table tr:last-child, body > table div table[width="755"], body > div table[width="755"] {
      display: none !important;
   }
   body > table #container table table td:last-child {
      display: none !important;
   }
   body > table #container table table td:first-child {
      width: 500px !important;
      text-align: center !important;
   }
   body > table table center, div.m span span a:last-child, #container > strong {
      display: none !important;
   }
   table[cellspacing="15"][align="center"] td {
      float: left !important;
      padding: 0 !important;
      max-width: 178px !important;
   }
   table[cellspacing="15"][align="center"] td div {
      float: none !important;
      height: 188px !important;
      width: 168px !important;
      empty-cells: hide !important;
   }
   .evilthumb {
      margin: 0 0 -1px 0 !important;
   }
   .m span a[href="http://tor.imageshack.us/tor/"].menu {
      display: none;
   }
}
