css:
@-moz-document domain(resetera.com) {
.pageWidth {
    padding-right: 5px;
    padding-left: 5px;
    margin: 0 auto;
    box-sizing: border-box;
    max-width: 97%;
    width: 97vw;
}
.PageNav a,
.pageNavLinkGroup .linkGroup a.OverlayTrigger {
    padding: 7px;
}
.messageText {
    color: rgb(222, 222, 222);
}
.messageUserBlock a.username {
    color: rgb(228, 228, 228);
}
.discussionListItem .itemPageNav {
    white-space: nowrap;
    margin: 0 10px;
    opacity: 100;
}
.itemPageNav a,
.itemPageNav span {
    font-size: 9px;
    padding: 0 3px;
    border-radius: 3px;
    display: inline-block;
    text-align: center;
}
body .itemPageNav a {
    color: rgb(214, 214, 214);
    background-color: rgb(28, 28, 28);
    padding: 3px;
    border: 1px solid rgb(24, 24, 24);
    border-radius: 3px;
}
.titleBar {
    background-color: inherit;
    background-image: none;
}
.titleBar h1 {
    color: #8e58c0;
}
.threadListSeparator {
    background-color: rgb(71, 57, 97);
}
.discussionListItem {
    background-color: rgb(44, 41, 41);
}
.navTabs .publicTabs .navLink {
    border: 1px solid rgb(71, 57, 97);
}
.quickReply {
    border: none;
}
.quickReply:before,
.quickReply:after {
    background-image: none;
}
.heading,
.xenForm .formHeader {
    background: rgb(86, 65, 132);
}
.pagenav-actions,
.button.primary {
    background: rgb(86, 65, 132);
}
.navLink .fa {
    color: #AFAFAF;
}
.navLink .fa:hover {
    color: rgb(86, 65, 132);
}
#logo img {
    height: 0 !important;
    width: 0 !important;
    padding-left: 250px !important;
    padding-top: 49px !important;
    background: url(https://storage.googleapis.com/resetera-filelocker/logo_new.png) no-repeat !important;
}
::selection {
    background: rgb(175, 155, 215);
}
a:link:hover {
    color: rgb(175, 155, 215);
}
.pollBlock .questionMark {
	background: rgb(85, 65, 132);
}
}
