}
.menu-tabHeader .tabs-tab {
    color: var(--FontMain);
}

/*bookmark*/
.bookmarkLink.bookmarkLink--highlightable.is-bookmarked:hover, .bookmarkLink.bookmarkLink--highlightable.is-bookmarked {
    color: var(--AccentMain);
}

/*Share Post*/
.tooltip--bookmark .tooltip-content, .tooltip--member .tooltip-content, .tooltip--share .tooltip-content {
    background: var(--backgroundDark);
}
/*Clipboard Icon*/
.shareInput-button {
    color: var(--AccentMain);
