/* Open Quote*/
.bbCodeBlock-expandLink a {
    color: var(--AccentSub);
}

/*checkbox colour*/
label.iconic:hover > input + i:before {
    color: var(--AccentSub);
}

/*sidepanel*/
a.sidePanel__tab {
    background-color: var(--AccentSub2);
}

.offCanvasMenu--nav .offCanvasMenu-linkHolder.is-selected a {
    color: var(--AccentSub);
}

/*Searchbar*/
.input:focus,
.input.is-focused {
    box-shadow: inset 0 0 0 1px var(--AccentSub2);
}

/*calendar*/
.pika-label,
.pika-table th {
    color: var(--AccentSub2);
}
.inputDate .inputDate-icon::before,
.inputNumber-button--up::before,
.inputNumber-button--down::before {
    color: var(--AccentSub2);
}

/*Polls*/
.pollResult-bar > i {
    background: var(--AccentSub2);
}

.pollResult--voted .pollResult-response:before {
    color: var(--AccentSub);
}

/*Filter Control panel*/
.FilterCP {
    background-color: var(--backgroundDark);
    border: 0px var(--AccentSub);
    color: var(--FontMain);
}

.TabLinksContainer div {
    background-color: var(--AccentSub2);
}

.ui-widget-content {
    border: none;
    background: var(--backgroundDark);
    color: var(--backgroundDark2);
}

input {
    background-color: var(--backgroundSub);
    color: var(--backgroundSub2);
    border-width: 0px;
    border-style: solid;
    border-color: var(--backgroundSub2);
}

.ui-widget-header {
    border: none;
    background: var(--backgroundSub);
    color: var(--backgroundSub2);
}

/*Thread Filter Section*/
.FilteringElementsContainer > div:nth-child(1) > strong:nth-child(1) {
    color: var(--AccentSub);
}

element.style {
    color: var(--FontMain);
}

select {
    background-color: var(--backgroundDark);
    color: var(--FontMain);
    border: none;
}

a[id*="RemoveThread"]:before {
    color: var(--FontMain);
}

/*Highlight*/
*::selection {
    background: var(--AccentSub2);
}

/*Posts*/
.message-cell.message-cell--main {
    background: var(--backgroundSub);
}
.bbCodeBlock-content {
    background: var(--backgroundDark2);
}
/*More posts*/
.message-cell.message-cell--alert {
    background: var(--backgroundAccent2);
}

.message-cell.message-cell--alert a {
    color: var(--AccentMain);
}

/*background colour*/
html {
    background: var(--backgroundDark);
}

.p-nav,
.uix_headerContainer .p-navSticky.is-sticky .p-nav {
    background: var(--backgroundDark);
    color: var(--FontMain);
}

/*Loading Box*/
.globalAction-block i {
    background-color: var(--AccentMain);
}

.globalAction-bar {
    background: var(--AccentMain);
}

::-webkit-scrollbar {
    background-color: #171717;
}

::-webkit-scrollbar-thumb {
    background-color: #4a4a4a;
}

/*New URL Hyperlink*/
.bbCodeBlock--unfurl {
    background: var(--backgroundDark);
}
}
