css:
@-moz-document url("https://www.playstationlifestyle.net"), url-prefix("https://www.playstationlifestyle"), domain("playstationlifestyle.net") {
body.custom-background {background-color: #000000; background-image: url(http://www.solidbackgrounds.com/images/1920x1080/1920x1080-black-solid-color-background.jpg)}
.clearfix.body {background: #00000000 url(http://www.solidbackgrounds.com/images/1920x1080/1920x1080-black-solid-color-background.jpg) repeat-x left -109px}
.left-nav {background-color: #000000}
.wrapper {width: 1340px}
#center {position: relative; float: right; margin-right: 220px; margin-bottom: 27px; width: 720px;}
.articleListItem, .highlightBox, .articleCommentBox {position: relative; float: left; overflow: hidden; margin-bottom: 12px; padding: 5px; min-height: 130px; width: 718px; border: 1px solid #000; background: #000000; color: #fff}
#header {background: url(http://www.solidbackgrounds.com/images/1920x1080/1920x1080-black-solid-color-background.jpg); position: relative; width: 815px; height: 109px}
.articleText {width: 855px; position: relative; display: inline-block}
#center img {max-width: 720px}
.articleListItem, .highlightBox, .articleCommentBox {position: relative; float: left; overflow: hidden; margin-bottom: 12px; padding: 5px; min-height: 130px; width: 1212px; border: 1px solid #000; background: #000000; color: #fff}
.coverpanel .bx-wrapper {display: none; position: relative;}
.articleListItem, .highlightBox, .articleCommentBox {width: 848px;}
.highlightBox {display:none}
.footer {display:none}
.articleTags, .articleListNavigation, .articleLatestPosts {border: 1px solid #000; background: #000}
#leftNavSocialBottom {background-color: #000}
.articleDate {color: #404040}
.articleTextContent {width: 915px}
.articleTextContent {border: 1px solid #0000; background: #00000000; color: #ffffff}
#widgetList>div {border: 1px solid #000; background: #000; color: #fff}
a {color: #7d7d7d; text-decoration: none}
blockquote {margin: 15px 30px 0 20px; padding-left: 20px; border-left: 1px solid #333; color: #fbfbfb}
.external-feed-content {display:none; background: #000000}
p.theByline {font-size: 12px; margin-top: 5px; display: inline-block; color: #4a4a4a}
.search-widget { position: absolute; right: -520px; bottom: 70px; padding: 0px}
.promo-head {position: absolute; right: 685px; top: 10px}
.header-social-icons {float: left; margin: 18px 15px 0px -70px}
*::-webkit-scrollbar { width: 10px ! important; height: 10px ! important; background: #000 ! important; border: 0px solid #252525 ! important}
*::-webkit-scrollbar-button {display: none ! important}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {border: 0 ! important; box-shadow: none ! important}
*::-webkit-scrollbar-thumb {min-height: 28px ! important; background: #202020 ! important}
*::-webkit-scrollbar-thumb:hover {background: #505050!important}
*::-webkit-scrollbar-thumb:active {background: #505050!important}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {background: #000 ! important}
#center {top: -55px}
#rightSide {top: -55px}
.disqus-count-text {display:none}
h1.specialTitle {background: #000000; color: #ffffff}
#center .gsc-control-cse .gsc-results.gsc-webResult .gsc-webResult.gsc-result td.gsc-table-cell-snippet-close .gs-bidi-start-align.gs-snippet {color: #ffffff}
#center .gsc-control-cse .gsc-results.gsc-webResult .gsc-cursor-box, #center .gsc-control-cse .gsc-results.gsc-webResult .gsc-webResult.gsc-result {border: 0px solid #000; background: #000000}
.gs-webResult.gs-result a.gs-title:visited, .gs-webResult.gs-result a.gs-title:visited b, .gs-imageResult a.gs-title:visited, .gs-imageResult a.gs-title:visited b {color: #202020}
.gs-webResult.gs-result a.gs-title:link, .gs-webResult.gs-result a.gs-title:link b, .gs-imageResult a.gs-title:link, .gs-imageResult a.gs-title:link b {color: #fff}
#center .gsc-control-cse .gsc-results.gsc-webResult .gsc-cursor-box .gsc-cursor-page {background-color: transparent; color: #202020}
}
