css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("charleston.net") {

/* clean up the top of the screen */
.padding_connectNav,
.connectNav,
#logoimage,
#trafficweatherzone,
#trafficzone,
#weatherzone,
#loginzone,
#classifiedtabzone,
#mastheadzonestatic,

/* clean up the main story column */
.photographer,

/* clean up the middle column  */

.columnrightnewsbox_300,        /* article tools */
.subheadlinebox,
.headlines,                     /* this is a ul */
*[bgcolor="#F1F1F1"],
*[width="232px"],
*[width="300px"],
*[width="380px"],

/* clean up the footer */
#footerzone

{display: none!important}

#commentszone
{display: inline!important}

*[id="basetable"],
table[width="750"],
table[width="740"],
td[width="400"]
{width: auto!important; padding-top: 100px; }

#mastheadzone
{height: 0px!important}



}
