css:
.blocklyMainBackground {
    fill: white !important;
}

.blocklyBlockBackground:first-of-type {
    fill: white;
    stroke: black;
    stroke-width: 2;
}

.blocklyBlockBackground ~ .blocklyBlockBackground {
    stroke: black;
    stroke-width: 5;
    filter: none;
}

.blocklyText, .blocklyEditableText > .blocklyText {
    fill: black;
}

.blocklyEditableText > rect {
    stroke: black;
    stroke-width: 2px;
}

.blocklyDropdownText {
    fill: black !important;
}

#blocklyDropdownArrowSvg {
    filter: brightness(0);
}

image[width="36px"] {
    /* LED icon */
    filter: invert(1) brightness(1);
}

image[width="24px"] {
    /* plus and minus icon */
    filter: invert(1);
}

image[id^="blocklyStringQuote"] {
    filter: brightness(0);
}

.blocklyLedOn {
    fill: black;
    stroke: black;
}

.blocklyLedOff {
    fill: white;
    stroke: black;
}

.blocklyPath[data-argument-type] {
    fill: white; /* will be overridden */
    stroke: black;
}

.blocklyPath[data-argument-type], .blocklyDisabled .blocklyBlockBackground:first-of-type {
