css:
@-moz-document url-prefix("http://www.dysfz.cc"), url-prefix("https://www.dysfz.cc"), url-prefix("http://www.dysfz.vip") {
/* 字体更改 */
html {
    font-family:-apple-system,BlinkMacSystemFont,Helvetica Neue,PingFang SC,Microsoft YaHei,Source Han Sans SC,Noto Sans CJK SC,WenQuanYi Micro Hei,sans-serif;
}
/* YUI 3.18.1 */
body {
    font-family: -apple-system,BlinkMacSystemFont,Helvetica Neue,PingFang SC,Microsoft YaHei,Source Han Sans SC,Noto Sans CJK SC,WenQuanYi Micro Hei,sans-serif;
}
html {
    background: #F5F5F5 !important;
}
/* 顶栏美化 */
.top {
    background-color: #2693EC;
    box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12);
}
.main {
    border-radius: 0;
    background-color: transparent;
}
.shadow {
    box-shadow: none;
}
.nav li a {
    height: 40px;
}
.nav li a:hover {
    color: #f5f5f5;
    border-bottom: 2px solid #2693ec;
    background-color: transparent;
}
.is-animate.style1>span {
    animation-name: none;
}
.top-search.fr {
    
    display: flex;
    display: -webkit-flex;
    justify-content: center;
    align-items: center;
}
.top-search input {
    display: none;
    border: none;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -moz-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -webkit-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -o-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    background-position: 100% !important;
    padding: 0 5px;
    height: 30px;
    background-color: rgba(255, 255, 255, 0.24);
}
.top-search input:focus {
    display: none;
    outline: 0;
    background-color: #e5f9f2;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.14), 0 2px 10px 0 rgba(0, 0, 0, 0.14);
}
.top-search button {
    display: none;
    background-color: #2677e1;
    color: #ffffff;
    border: none;
}
/* 登录&注册 */
.acc a:hover {
    text-decoration: none;
    color: #f5f5f5 !important;
    border-bottom: 2px solid #2693ec;
}
/* 首页卡片 可改宽度等 */
.movie-list li {
    width: 700px;
    left: -100px;
    position: relative;
    border: none;
    background: #fff;
    padding: 20px 30px;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    margin-top: 25px;
}
.movie-list {
	margin-top: -10px;
}
.movie-list li,
.bar dl,
.fl .main.shadow,
.pageturn #tiao-btn,
.pageturn a,
.pageturn b {
    box-shadow: 0 0 4px rgba(0, 0, 0, .1), 0 4px 8px rgba(0, 0, 0, .1);
    border-radius: 10px;
}
.movie-list li:hover,
.bar dl:hover {
    box-shadow: 0 0 4px rgba(0, 0, 0, .1), 0 4px 8px rgba(0, 0, 0, .1);
}
.movie-list li h2 a:hover{
	color:#000000
}
.movie-list .des .txt {
    width: 550px;
}
.movie-list .des .pic {
    margin-left: 0;
}
.bar.fr .shadow:nth-child(1) {
    display: none;
}
/* 正中搜索框 可选去除，有bug */
.search input {
    background-color: #ffffff;
    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.1), 0 0 0 1px rgba(0, 0, 0, 0.04);
    border: none;
    border-radius: 3px;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -moz-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -webkit-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -o-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    background-position: 100% !important;
    z-index: -1;
    height: 36px;
    padding: 4px 0;
    margin: 10px
}
.search {
    width:850px;
    margin: 20px auto;
}
.search input{
	width:700px;
	height:36px;
	font-size:20px;
	text-indent:10px;
	border:0px;
	vertical-align: middle;
}
.search input:focus {
    outline: 0;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
}
.search button {
    height: 43px;
    -webkit-border-radius: 2px;
    -webkit-user-select: none;
    border-radius: 2px;
    color: #f5f5f5;
    background-color: #2693EC;
    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.1), 0 0 0 1px rgba(0, 0, 0, 0.04);
    font-size: 16px;
    font-weight: bold;
    min-width: 54px;
    padding: 0 16px;
    text-align: center;
}
.search button:hover {
    background-image: -webkit-gradient(linear, left top, left bottom, from(#2693EC), to(#2693EC));
    background-image: -webkit-linear-gradient(top, #2693EC, #2693EC);
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
    background-color: #2693EC;
    box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.1), 0 0 0 1px rgba(0, 0, 0, 0.04);
    background-image: linear-gradient(top, #2693EC, #2693EC);
    background-image: -o-linear-gradient(top, #2693EC, #2693EC);
    border: 1px solid #c6c6c6;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
    color: #f5f5f5;
}
.sort-box {
    top: 0;
    right: 20px;
}
/* 右栏悬浮 */
.bar dl {
    width:350px;
	padding:7px 5px;
	margin: 0 0 -20px;
    margin-top: 35px;
	border-radius:10px;
	background-color: #fff;
	background-color:rgba(255,255,255,.96); 
}
.bar dt {
	padding: 3px 5px;
	font-weight: bold;
	border-bottom: 1px #ccc solid;
}
.element.style {
    display: none;
}
.bar dd {
	margin:5px 0 0;
	padding: 5px;
}
.cate li {
    width:300px;
	margin-bottom: 2px;
}
.cate li a {
    width:300px;
}
.cate li a:hover {
    width:300px;
	color: #fff;
	background-color: #6cb5f4;
}
.tag a {
	display: block;
	float: left;
	margin-right: 5px;
	margin-bottom: 5px;
	padding: 0 8px;
	line-height: 22px;
	color: #000;
	font-size: 93%;
	background-color: rgba(220,220,220,.5);
	border-radius: 3px;
}
.tag a:hover {
	color: #fff;
	background-color: rgba(20,20,20,.5);
	text-decoration: none;
}
#sortlist {
    border: none;
    -webkit-box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
    box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
}
#sortlist a {
    -webkit-font-smoothing: antialiased;
    font-size: 1rem;
    text-decoration: inherit;
}
#sorthand {
    background-color: transparent;
    color: #212121;
    padding: 3px 1px;
    border-radius: 0;
    border-bottom: 1px solid rgba(0, 0, 0, 0.22);
    text-decoration: inherit;
}
/* 详情页 */
.detail {
	width: 670px;
	padding: 20px 45px;
	line-height: 26px;
	word-wrap: break-word;
}
.fl .main.shadow  {
    width:760px;
    background-color: #fff;
    margin:50px 0px 0px -80px;
}
.fl .main.shadow h1 {
    font-size: 1.3em;
    line-height: 1.3em;
}
.fl .main.shadow dd {
    margin: 0;
}
.comments textarea, .comments div {
	width: 705px;
	height: 66px;
	border: 1px #6CB5F4 solid;
	border-radius: 5px;
	padding: 5px;
	font-size: 97%;
	line-height: 22px;
	margin: 10px 0 5px;
	background-color: #fff;
}
.main h1 {
    padding: 15px 20px;
}
.span class {
    font-family:-apple-system,BlinkMacSystemFont,Helvetica Neue,PingFang SC,Microsoft YaHei,Source Han Sans SC,Noto Sans CJK SC,WenQuanYi Micro Hei,sans-serif;
}  
.inseparable a.fl,
.inseparable a.fr {
    width: 20%;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
}
.info {
    padding: 0;
    padding-left: 16px;
    margin: 0 20px;
    border-left: 3px solid #ddd;
}
.resources,
.bdsharebuttonbox:nth-of-type(2),
.fl .main.shadow dt strong {
    display: none;
}
.fl .main.shadow dd {
    margin: 0;
}
.fl .main.shadow ul {
    list-style: none;
    padding: 0;
}
/* 加入收藏去除 */
.fl .main.shadow .marks {
    display: none;   
}
.pageturn a,
.pageturn b {
    padding: 2px 8px;
    border: none;
    color: #212121;
    background: white;
}
.pageturn b {
    border: 1px solid #2693EC;
    color: white;
    background: #2693EC;
}
.pageturn div[style="float:right; font-size:14px;"] {
    padding-top: 2px;
    padding-bottom: 2px;
}
.pageturn div[style="float:right; font-size:14px;"] input {
    border: none;
    border-bottom: 1px solid rgba(0, 0, 0, .12);
    background: transparent;
}
.pageturn div[style="float:right; font-size:14px;"] input:focus {
    outline: 0;
    border-bottom: 2px solid #2693EC;
}
.pageturn #tiao-btn {
    height: 20px;
    padding: 2px 8px;
    text-align: center;
    text-decoration: none;
    background: white;
    color: #212121;
    border: none;
}
.dbscore,
.imdbscore {
    color: #fff;
    border-radius: 2px;
    padding: 6px 8px;
    background: #26ace7;
    margin-top: 7px;
}
.dbscore b,
.imdbscore b {
    color: #f5f5f5;
    font-weight: 500;
}
/* 顶栏去除 */
.nav-top-hao {
    display: none
}
.nav-top-xsldh {
    display: none
}
.nav-top-more {
    display: none
}
/* 迅雷电影下载字样去除 */
.xunlei {
    display: none
}
/* 淘宝广告去除 */
.item-item {
	display: none
}
.item-second-dl {
	display: none
}
/* 天猫广告去除 */
.adv-1 {
    display: none
}
/* 空间&微博等分享去除 */
.bdsharebuttonbox {
    display: none
}
/* 页脚去除 */
.footer {
    display: none
}
/* 多余文字去除 */
.affix {
    display: none
}
}

@-moz-document url-prefix("http://www.dysfz.cc/my/"), url-prefix("https://www.dysfz.cc/my/") {
.wraper >.menu.shadow {
    width: 280px;
    top: 42px;
    left: 0;
    background-color: #fff;
    box-shadow: 0 0 4px rgba(0, 0, 0, .28);
    padding-top: 40px;
    border-radius: 0;
    height: 100vh;
}
.avatar {
    padding: 24px;
}
.avatar b {
    font-size: 24px;
    line-height: 40px;
}
.menu ul {
    background: transparent;
    margin-top: 16px;
}
.menu ul li {
    line-height: 40px;
    height: 40px;
    border-top: none;
    text-align: left;
    padding-left: 24px;
}
.menu ul li a {
    width: 100%;
    height: 100%;
}
.menu ul li:hover {
    background: rgba(0, 0, 0, 0.12);
}
.menu ul li a:hover {
    background: transparent;
}
.wraper >.sticky.shadow {
    position: fixed;
    left: 0px;
    top: 450px;
    width: 240px;
    background: transparent;
    border-radius: 0;
    z-index: 999;
    padding: 24px;
}
.wraper .my-main {
    padding: 40px;
    margin-left: 300px;
    width: 1100px;
}
.wraper .my-main >.shadow {
    border-radius: 2px !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
}
#picup {
    height: auto;
}
.main_pick {
    padding: 0;
    margin: 0;
    padding: 16px;
}
.main_pick li {
    display: flex;
    display: -webkit-flex;
    justify-content: space-between;
    align-items: center;
    width: 80%;
    margin: 0;
    border: none;
    box-shadow: none;
    height: 42px;
}
.main_pick li a {
    width: 90%;
}
.main_pick li a h4 {
    width: auto;
    text-indent: 0;
    margin-top: 0;
    margin-bottom: 0;
    width: 100%;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}
.main_pick li .button {
    position: unset;
    margin-left: 16px;
    order: 2;
}
.main_pick li .button a {
    height: auto;
}
}
