css:
@namespace url(http://www.w3.org/1999/xhtml);
.tg_head_split {
    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2),
                0 8px 10px 1px rgba(0, 0, 0, .14),
                0 3px 14px 2px rgba(0, 0, 0, .12);
  }

  .im_page_wrap {
    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2),
                0 8px 10px 1px rgba(0, 0, 0, .14),
                0 3px 14px 2px rgba(0, 0, 0, .12);
  }

  .im_page_split {
    background: #e7ebf0 url(http://i.imgur.com/WdGuNvY.jpg) fixed;
  }

  li.im_dialog_wrap > a {
    will-change: background-color;
    transition: background-color 150ms ease-in;
  }

  li.im_dialog_wrap > a * {
    will-change: color;
    transition: color 200ms ease-in;
  }

  .im_history_wrap {
    background: linear-gradient(180deg, transparent, 70%, transparent, rgba(255, 255, 255, .6));
  }

  .im_bottom_panel_wrap {
    background: linear-gradient(180deg, rgba(255, 255, 255, .6), 60%, rgba(255, 255, 255, .8));
  }

  .im_history_messages {
    text-shadow: 0 1px white;
  }

  .im_history_messages [ng-switch-when="sticker"] {
    filter: drop-shadow(0 0 6px white) drop-shadow(0 0 7px white);
  }

  .im_record {
    background-color: rgba(255, 255, 255, .4);
  }

  .im_record:hover {
    background-color: rgba(255, 255, 255, .7);
  }
.im_content_message_wrap {
    margin: 8px 10px 8px 16px;

}
.tg_head_split {
    max-width: 100%;
    margin: 0 auto;
    height: 48px;
    background: #5682a3;
}
.im_page_wrap {
    background: #fff;
    max-width: 100%;
    max-height:100%;
    height:100%;

}
.im_dialogs_scrollable_wrap .im_dialog_wrap {
    margin-top: 0;
    background: #fff; /*#eaedf5*/
}
.im_bottom_panel_wrap {
    background: #fff;
}
.im_history_pinned_wrap {
    background: #fff;
}
.im_page_wrap {
    box-shadow:unset ;
}
im_content_message_wrap {
    direction: ltr;
}
.im_message_document_caption, .im_message_photo_caption, .im_message_video_caption {
    direction: rtl;
}
body.non_osx {
    font: 13px/18px iransans,Tahoma,sans-serif,Arial,Helvetica;
        font-weight: normal;
}

.im_dialogs_panel {
    background: #fff;
}
.im_dialogs_col_wrap {
    background: #fff;
}
a.im_message_fwd_photo {
    position: inherit;
    margin: 5px;
}


/*--------------------------------------------------------------------------------------*/

/* i really want this to be global */
.tg_head_split, .im_page_wrap, .im_message_wrap, .im_send_panel_wrap, .im_send_panel_wrap {
	max-width: 100%
}

.audio_player_title_wrap, .audio_player_title {
	max-width: none;
	display: inline;
}

.im_message_body {
	background-color: #fff;
	border-radius: 5px;
	/*     padding: 1em; */
}

.im_message_author_wrap {
	color: #fff;
	display: block;
	padding: 1px 5px;
}

.im_bot_intro_message_wrap {
	background-color: #f0f4f7;
	border-radius: 1em;
}

.im_message_date_split_text {
	font-weight: bold;
}

div.im_message_author, div.im_message_body {
	display: inline-block;
	max-width: 500px;
}

.im_message_author {
	margin: 0px !important;
}

.im_content_message_wrap {
	text-align: right;
}

.pull-left {
	float: right!important
}

.pull-right {
	float: left!important
}

a.im_message_contact_photo, a.im_message_from_photo {
	margin: 0 15px;
}

.im_grouped_short .im_message_author_wrap, .im_grouped_short .im_message_from_photo, .im_grouped .im_message_author_wrap, .im_grouped .im_message_from_photo, .im_grouped_fwd .im_message_author_wrap, .im_grouped_fwd .im_message_from_photo, .im_grouped_fwd_short .im_message_author_wrap, .im_grouped_fwd_short .im_message_from_photo {
	display: block !important;
}

.im_dialogs_col_wrap {
	border-right: none;
	border-left: 2px solid #E9EBED;
	float: right;
}

div.im_dialog_photo {
	margin: 0 15px;
}

.im_dialogs_col_wrap .emoji {
	font-size: 0px;
	vertical-align: middle;
}

.nav > li > a {
	text-align: right;
}

.im_grouped_short .im_message_body, .im_grouped .im_message_body, .im_grouped_fwd .im_message_body, .im_grouped_fwd_short .im_message_body {
	margin-left: 0;
	margin-right: 70px;
}

.emoji {
	font-size: 0;
	vertical-align: -webkit-baseline-middle;
}

pre {
	background-color: #2f343d;
	color: #ddd;
	padding: 1em 2em;
	text-align: left;
	direction: ltr;
}

.im_grouped_short .im_content_message_wrap {
	/*     margin: 2px; */
	/*     margin: 0px 1em; */
}

.im_grouped_short .im_message_body, .im_grouped .im_message_body, .im_grouped_fwd .im_message_body, .im_grouped_fwd_short .im_message_body {
	/*     padding: 0.5em 1em; */
}

.im_service_message {
	background: #bfbfbf4d;
	border-radius:5px;
}

.im_history_message_wrap .im_message_body {
	transition: 0.3s;
	border: 1px solid transparent
}

.im_message_fwd_from {
	text-align: left
}

.im_message_reply {
	background: #fff;
	overflow: hidden;
	position: relative;
}

.im_message_document_info {
	text-align: left
}

.im_message_audio, .im_message_document, .im_message_upload_file, .im_message_video {
	overflow: hidden;
	margin-bottom: 1em;
}

a.im_message_photo_thumb, a.im_message_video_thumb {
	margin: 1em 1em
}

.icon-message-views {
	vertical-align: middle;
}

.im_message_views_wrap {
	width: 100%
}

.im_message_views {
	top: -2em;
	left: 0
}

.im_message_wrap {
	width: 80%;
}

.im_dialog_meta {
	text-align: left;
}

.im_message_in .pull-left {
	float: left!important;
}

.im_message_in .pull-right {
	float: right!important;
}

.im_grouped_short .im_message_in .im_message_body, .im_grouped .im_message_in .im_message_body, .im_grouped_fwd .im_message_in .im_message_body, .im_grouped_fwd_short .im_message_in .im_message_body {
	margin-right: 0;
	margin-left: 0px;
}

.im_grouped_short .im_message_body, .im_grouped .im_message_body, .im_grouped_fwd .im_message_body, .im_grouped_fwd_short .im_message_body {
	margin-left: 0;
	margin-right: 0px;
}

.im_message_meta {
	position: absolute;
	right: 0;
	bottom: 0px;
}

.im_message_date, .im_message_fwd_date {
	color: #666;
}

.im_content_message_wrap {
	position: relative;
	/*     margin:0; */
}

.im_message_date, .im_message_fwd_date {
	padding: 0
}

.im_message_in .im_message_meta {
	left: 0;
	right: inherit;
}

.im_message_in.im_content_message_wrap {
	text-align: left;
}

.im_message_text, .im_message_media {
	margin: 5px 10px!important;
	font-size: 14px;
}

.im_message_reply_wrap {
	margin: 10px 10px!important;
	padding: 5px;
}

.im_message_in .im_message_text {
	text-align: right;
}

.im_message_in .im_message_document_caption, .im_message_in .im_message_photo_caption, .im_message_in .im_message_video_caption {
	text-align: right
}

.im_message_reply_border {
	height: 100%;
}
