css:
@-moz-document domain("riot.im") {
body, .mx_RightPanel_headerButtonGroup, .mx_MatrixChat .mx_LeftPanel, .mx_RoomSubList_label, .mx_RoomTile, .mx_RoomDropTarget_container, .mx_RoomDropTarget {
	background-color: #444;
}
.mx_RoomTile_selected {
	background-color: #636363;
}
.mx_RoomTile_unread .mx_RoomTile_name, .mx_RoomTile_selected, .mx_EventTile_content .markdown-body, .mx_EventTile_body {
	color: #fff;
}
.mx_MatrixChat .mx_MatrixChat_middlePanel, .mx_RoomHeader_rightRow, .mx_RoomView_callStatusBar .mx_UploadBar_uploadProgressInner, .mx_RoomView_statusArea {
	background-color: #3a3a3a;
}
.mx_TagPanel {
	background-color: #222;
}
path#Oval-1-Copy-7 {
	fill: #73ba25 !important;
}
path#line {
	fill: #fff;
}
#mx_theme_accentColor, a:hover, a:link, a:visited, .mx_RightPanel_headerButton_badge, .mx_RoomSubList_roomCount, .mx_MemberEventListSummary_toggle, .mx_EventTile_content .markdown-body a {
	color: #73ba25;
}
.mx_EventTile_highlight, .mx_EventTile_highlight .markdown-body {
	color: #00a489;
}
.mx_RightPanel_headerButton_highlight, .mx_RoomHeader_textButton:hover, .mx_RoomSettings_leaveButton:hover, .mx_RoomSettings_unbanButton:hover {
	background-color: #73ba25;
}
.mx_RoomHeader_textButton, .mx_RoomSettings_leaveButton, .mx_RoomSettings_unbanButton {
	background-color: #6da741;
}
.mx_MessageComposer_input_wrapper::selection, .mx_MessageComposer_input_wrapper::-moz-selection {
	background: #73ba25;
}
.mx_RoomSubList_chevronDown {
	border-top: 6px solid #73ba25;
}
.mx_RoomSubList_chevronRight {
	border-left: 6px solid #73ba25;
}
.mx_SearchBox, .mx_TagPanel .mx_TagPanel_divider, .mx_LeftPanel .mx_BottomLeftMenu {
	border-color: hsla(89, 48%, 64%, .2);
}
.mx_RoomDropTarget, input[type="password"]:focus, input[type="text"]:focus, textarea:focus, hr.mx_RoomView_myReadMarker {
	border-color: #73ba25;
}
}
