@-moz-document domain("linuxrocks.online"), regexp("(http(s)?:\\/\\/)?(mastodon.+)") {
/*--------| background |--------*/

.react-toggle--checked .react-toggle-track, .dropdown-menu__item a:active, .dropdown-menu__item a:focus, .dropdown-menu__item a:hover, .simple_form .block-button:hover, .simple_form .button:hover, .simple_form button:hover, .admin-wrapper .sidebar ul ul a.selected:hover, .privacy-dropdown__option.active:hover, .react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track, .button:active, .button:focus, .button:hover, .reply-indicator__content .status__content__spoiler-link:hover, .status__content .status__content__spoiler-link:hover {
	background-color: #73ba25;
}
.simple_form .block-button, .simple_form .button, .simple_form button, .admin-wrapper .sidebar ul ul a.selected, .privacy-dropdown__option.active, .privacy-dropdown__option:hover, .privacy-dropdown.active .privacy-dropdown__value.active, .button, .reply-indicator__content .status__content__spoiler-link, .status__content .status__content__spoiler-link {
	background-color: #6da741;
}
.reply-indicator {
	background-color: #bceb99;
}
.dropdown-menu, .dropdown-menu__item a, .dropdown-menu {
	background-color: #fff;
}
.emoji-mart-bar:first-child {
	background-color: #e6e6e6;
}
.ui, .admin-wrapper .sidebar-wrapper, .flash-message, .privacy-dropdown__dropdown {
	background-color: #636363;
}
.column-link:hover {
	background-color: #555;
}
.drawer__inner, .column-link, .detailed-status, .detailed-status__action-bar, .column-header__button.active, .column-header__button.active:hover, .column-header__collapsible-inner, .keyboard-shortcuts kbd {
	background-color: #4a4a4a;
}
.column-header, .column-header__button, .column-header__back-button, .column-inline-form, .search-results__header, .drawer__header, .load-more:hover, .focusable:focus, .account__header > div, .column-back-button, .admin-wrapper .sidebar ul a.selected, .admin-wrapper .sidebar ul a:hover {
	background-color: #444;
}
.column > .scrollable, .getting-started, .column-link__badge, .column-subheading, .drawer__inner.darker, .search__input, .search__input:focus, .drawer__header a:hover, .empty-column-indicator, .error-column, .search-results__section h5 span, .account__header .account__header__fields dd, body.admin, .admin-wrapper .sidebar ul ul, .compose-form .compose-form__buttons-wrapper {
	background-color: #3a3a3a;
}
.account__section-headline, .account__header .account__header__fields dt {
	background-color: #333;
}
.column-header__wrapper.active::before {
	background: radial-gradient(ellipse,#73ba253b 0,rgba(43,144,217,0) 60%);
}

/*--------| color |--------*/

.icon-button.active, .notification__message .fa, .column-header__back-button, .getting-started a, .column-header > .column-header__back-button, .column-header.active .column-header__icon, .account__header__fields a, .account__header .account__header__username, .column-back-button, .attachment-list__list a, .attachment-list.compact .fa, .privacy-dropdown__option__content, .muted .status__content a, .muted .status__content p, .muted .status__display-name strong, .reply-indicator__content a, .status__content a, .search__icon .fa-times-circle:hover {
	color: #73ba25;
}
.search__icon .fa-times-circle {
	color: #6da741;
}
.column > .scrollable, .getting-started, .column-link__badge, .column-subheading, .drawer__inner.darker, .search__input, .search__input:focus, .setting-text, .column-settings__section, .text-btn:hover, .empty-column-indicator, .error-column, .search-results__section h5 span, .flash-message, .privacy-dropdown__option.active, .privacy-dropdown__option, .privacy-dropdown__option__content strong, .setting-meta__label, .setting-toggle__label {
	color: #fff;
}
.notification__message, .account .account__display-name, .search-results__header, .column-header__button, .account__section-headline a, .account__action-bar__tab > span, .drawer__tab, .text-btn, .account__header .account__header__fields dt, .simple_form span.hint, .admin-wrapper .content h4, .admin-wrapper .sidebar ul a, .icon-button.inverted, .text-icon-button, .icon-button:active, .icon-button:focus, .icon-button:hover {
	color: #bbb;
}
.icon-button, .navigation-bar a, .detailed-status__meta, .text-icon-button:active, .text-icon-button:focus, .text-icon-button:hover, .icon-button.inverted:active, .icon-button.inverted:focus, .icon-button.inverted:hover {
	color: #999;
}
.status__prepend, .status__prepend .status__display-name strong, .status__display-name, .status__relative-time, .account__disclaimer, .emoji-mart-search input, .load-more {
	color: #888;
}
.dropdown-menu__item a, .dropdown-menu__item a:active, .dropdown-menu__item a:focus, .dropdown-menu__item a:hover, .status__content__spoiler-link {
	color: #222;
}

/*--------| border color |--------*/

.setting-text:active, .setting-text:focus {
	border-color: #73ba25;
}
.react-toggle--checked .react-toggle-thumb {
	border-color: #6da741;
}
.setting-text {
	border-color: #aaa;
}
.account, .status, .detailed-status__action-bar, .search-results__section h5::before, .admin-wrapper .content h4, .account__disclaimer, .account__section-headline {
	border-color: #555;
}
.account__section-headline a.active::after, .account__section-headline a.active::before {
	border-color: transparent transparent #555;
}
.account__header .account__header__fields dl, .account__header__fields, .account__action-bar__tab, .account__action-bar {
	border-color: #4a4a4a;
}
.account__section-headline a.active::after {
	border-color: transparent transparent #3a3a3a;
}
.keyboard-shortcuts kbd {
	border-color: #222;
}

/*--------| other stuff |--------*/

.column-header.active {
	-webkit-box-shadow: 0 1px 0 #73ba254d;
	box-shadow: 0 1px 0 #73ba254d;
}
.column-header.active .column-header__icon {
	text-shadow: 0 0 10px #73ba2566;
}
