css:
@-moz-document domain("wikipedia.org") {
#mw-page-base {
	background-image: linear-gradient(#636363 50%, #4a4a4a 100%);
}
.mw-body, .parsoid-body {
	border-color: #6da741;
}
.pure-button-primary-progressive {
	background-color: #73ba25;
	border-color: #6da741;
}
.pure-button-primary-progressive:hover {
	background-color: hsl(89, 67%, 50%);
	border-color: #6da741;
}
.lang-list-button, .mw-body, .parsoid-body {
	background-color: #3a3a3a;
}
a, #mw-panel .portal .body li a, .mw-parser-output a.external, .mw-parser-output a.extiw, .mw-parser-output a.extiw:active, .lang-list-button {
	color: #6da741;
}
a:visited, #mw-panel .portal .body li a:visited, .mw-parser-output a.external:visited {
	color: #00a489;
}
.mw-parser-output a.external {
	color: #21a4df;
}
div.vectorTabs {
	background: linear-gradient(#636363, #6da741);
}
div.vectorTabs span {
	background: none;
	border-right: 1px solid;
	border-color: rgba(0, 0, 0, 0);
}
div.vectorTabs li.selected span {
	background: linear-gradient(#636363, #3a3a3a);
}
div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
	border-bottom: none;
}
div.vectorTabs ul li {
	background: linear-gradient(#636363 30%, #1a2a09 150%);
}
div.vectorTabs li a {
	color: #73ba25;
	border-bottom: 1px solid #6da741;
	margin-top: -1px;
}
#ca-watch {
	margin-top: -1px !important;
}
.catlinks, table.ambox, div.thumbinner, table.wikitable, .navbox, .navbox-subgroup, table.wikitable > tr > th, table.wikitable > * > tr > th {
	background-color: #4a4a4a;
}
#mw-panel .portal .body {
	background: none;
	border-top: 1px solid #222;
}
#p-personal a.new {
	color: #ba0000 !important;
}
table.jquery-tablesorter th.headerSort {
	background: #444;
}
.navbox-abovebelow, th.navbox-group, .navbox-subgroup .navbox-title {
	background-color: #636363;
}
.navbox-list {
	border-color: #636363;
}
.keyboard-key {
	border-color: #4a4a4a !important;
	background: #222 !important;
}
.mw-highlight {
	background-color: #444;
}
.client-js .mw-content-ltr .mw-editsection-bracket:first-of-type, .client-js .mw-content-rtl .mw-editsection-bracket:not(:first-of-type), .client-js .mw-content-rtl .mw-editsection-bracket:first-of-type, .client-js .mw-content-ltr .mw-editsection-bracket:not(:first-of-type) {
	color: #999;
}
#mp-topbanner, #mp-left, #mp-right, #mp-middle, #mp-lower, .vertical-navbox, body, #huvudsidaintro {
	background-color: #4a4a4a !important;
}
#mp-topbanner, #mp-left, #mp-right, #mp-middle, #mp-lower, .vertical-navbox, table.wikitable * {
	border-color: #222 !important;
}
#mp-tfa-h2 {
	background-color: hsl(147, 10%, 30%) !important;
	border-color: hsl(150, 10%, 40%) !important;
}
#mp-itn-h2 {
	background-color: #41464c !important;
	border-color: #565d66 !important;
}
#mp-dyk-h2 {
	background-color: hsl(147, 10%, 30%) !important;
	border-color: hsl(150, 10%, 40%) !important;
}
#mp-otd-h2 {
	background-color: hsl(213, 10%, 30%) !important;
	border-color: hsl(214, 10%, 40%) !important;
}
#mp-tfl-h2 {
	background-color: hsl(338, 10%, 30%) !important;
	border-color: hsl(334, 10%, 40%) !important;
}
#mp-tfp-h2 {
	background-color: hsl(262, 10%, 30%) !important;
	border-color: hsl(266, 10%, 40%) !important;
}
#mp-tfp-h2, #mp-tfl-h2, #mp-otd-h2, #mp-dyk-h2, #mp-itn-h2, #mp-tfa-h2, #mp-topbanner, #mp-left, #mp-right, #mp-middle, #mp-lower {
	color: #eee !important;
}
.lang-list-button {
	outline-color: #4a4a4a;
}
.lang-list-container, .lang-list-active .lang-list-button, .lang-list-button:hover, .pure-form input[type="search"], .bookshelf span, table.fmbox-system {
	background: #444;
}
.lang-list-border {
	background: #222;
}
.bookshelf {
	box-shadow: none;
	border-color: #eee;
}
.lang-list-active .lang-list-button {
	outline-color: #4a4a4a;
}
.pure-form input[type="search"] {
	box-shadow: none;
}
.search-container .js-langpicker-label {
	color: #bbb;
}
.mw-parser-output .infobox, .pure-form input[type="search"], .central-featured-lang small, .other-project-tagline, .footer-sidebar-text, .bookshelf span, textarea, div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited, .mw-highlight, #mw-panel .portal h3, #footer ul li, table.wikitable, h1, h2, h3, h4, h5, h6, .tocnumber, .infobox, .mw-body, .parsoid-body {
	color: #eee;
}
.mw-parser-output .navbox, .mw-parser-output .navbox-subgroup {
	border-color: #222 !important;
}
.mw-parser-output .navbox-abovebelow, .mw-parser-output .navbox-group, .mw-parser-output .navbox-subgroup .navbox-title {
	background-color: #636363;
}
.mw-parser-output .navbox-list, #huvudsidaintro {
	border-color: #222 !important;
}
#huvudsidaintro {
	background-image: none;
}
.mw-parser-output .frontPageRight .frontPageBlock, li.gallerybox div.thumb, .mw-parser-output .infobox, .mw-parser-output .navbox, .mw-parser-output .navbox-subgroup, textarea, .infobox, .toc, .mw-warning, .toccolours, .app-badges {
	background-color: #4a4a4a;
}
.mw-parser-output .frontPageBlock, .mw-parser-output .frontPageRight .frontPageBlock, li.gallerybox div.thumb, .mw-parser-output .infobox, .styled-select:hover, .pure-form input[type="search"], .lang-list-active .lang-list-button, .lang-list-button:hover, textarea, .infobox, .toc, .mw-warning, .toccolours, .lang-list-button, .mw-highlight, html .thumbimage, div.thumbinner, table.ambox, .catlinks, .infobox.geography .mergedtoprow td, .infobox.geography .mergedtoprow th, .navbox, .navbox-subgroup {
	border-color: #222;
}
}
