css:
@-moz-document domain(stud42.fr) 
  {
  .row
  {
      background-color: #1a1e21;
      color: white;
  }

  .page-container [appContent="cluster"] .head, .page-container [appContent="friends"] .head
  {
      background: black;
  }

  html
  {
       background-color: black;   
  }

  .page-container
  {
      background-color: #1a1e21;
  }

  #news
  {
      background: black;
      border : none;
  }

  .page-container [appContent="cluster"] .table, .page-container [appContent="friends"] .table
  {
      background : #1a1e21!important;
      color: white;
  }
  body > header
  {
      border-bottom: none;
  }

  #infoModal .modal-body, #infoModal .modal-body div.row > div, #infoModal .modal-body #hoverBlueData, .modal-footer, .thumber, .page-container [appContent="cluster"] .list-thumber, .page-container [appContent="friends"] .list-thumber
  {
      background :#1a1e21;
      color :white;
  }

  td[pillar]
  {
      background: #0000 !important;
  }
  .btn-simple
  {
   color: white;   
  }
  .table table td
  {
      border-right: none !important;
      border-bottom: none !important;   
  }

  .page-container [appContent="cluster"] .table table td:first-of-type, .page-container [appContent="cluster"] .table table td:last-of-type, .page-container [appContent="friends"] .table table td:first-of-type, .page-container [appContent="friends"] .table table td:last-of-type
  {
      background: #0000  !important;   
  }

  .page-container [appContent="cluster"] .head, .page-container [appContent="friends"] .head 
  {
      border: none !important;   
  }

  .page-container [appContent="cluster"] .table table td[connected], .page-container [appContent="friends"] .table table td[connected]
  {
      background-color: #1d3320;
  }

  .page-container [appContent="cluster"] .table table td[connected][friend], .page-container [appContent="friends"] .table table td[connected][friend]
  {
      background-color: #167095;
  }

  .popover
  {
      color: #cccccc;
      background-color: #1a1e21;
  }

  .popover h6
  {
      display: none;
  }

  #search, #addFriend
  {
      border-color: #555555;
      background-color: #222222;
      color: #cccccc;
  }

  .page-container [appContent="cluster"] .thumber .thumbs, .page-container [appContent="friends"] .thumber .thumbs
  {
      border-color: #444444;
  }

  .highcharts-background
  {
      fill: #1a1e21;
  }
  #highcharts-4 .highcharts-graph
  {
      stroke: #FFFFFF;
  }

  tspan
  {
      fill: #cccccc !important;
  }

  .header_panel
  {
      background-image: url("https://signin.intra.42.fr/assets/background_login-a4e0666f73c02f025f590b474b394fd86e1cae20e95261a6e4862c2d0faa1b04.jpg") !important;
  }
  .page-container [appContent="cluster"] .table table td[pillar], .page-container [appContent="friends"] .table table td[pillar]
  {
    background : #1f252a !important;
  }
  .page-container [appContent="cluster"] .table table td, .page-container [appContent="friends"] .table table td
  {
    color: #666;
  }
    }
