css:
@-moz-document domain("mastodon.social") {
/* commented for ur convenience, very messy and work-around-y, might break at any time.
opening links in a new window/tab will break things, refresh to fix it */

/* column colours */
.item-list { background: #000; }
article { background:rgba(255,255,255,12%);	}
.focusable:focus {background:rgba(255,255,255,5%); }

/* permalink column colours probably */
.column > .scrollable { background:rgba(255,255,255,5%);	}
.detailed-status { background-color:inherit;	}
.detailed-status__action-bar { background-color:inherit;	}
.focusable:focus > .detailed-status { background-color:rgba(255,255,255,0%);	}
.focusable:focus > .detailed-status__action-bar { background-color:rgba(255,255,255,0%);	}

/* colour of dms */
.status.status-direct { background-color:rgba(255,255,255,5%);	}
.focusable:focus > .status.status-direct { background-color:rgba(255,255,255,10%);	}

/* getting started zone */
.getting-started__wrapper { background-color:inherit;	}
.column-subheading { background-color:rgba(255,255,255,5%);	}
.column-link { background-color:rgba(255,255,255,10%);	}
.column-link:hover { background-color:rgba(255,255,255,12%);	}
.static-content { background-color:rgba(255,255,255,5%);	}

/* sidebar */
.drawer { max-width:350px; margin-top:600px; margin-left:60px;	}
nav.tabs-bar { border-top:2px solid #038387;	}
.drawer__inner { 
	background:rgba(255,255,255,10%);
	border-top: 2px solid #038387;	}
.compose-form .autosuggest-textarea { min-height:170px; background-color:#fff;	}

/* replies & warnings */
.reply-indicator { border-radius:0; background-color:#038387;	}
.reply-indicator a { color:#111;	}
.compose-form .compose-form__warning { border-radius:0; background-color:#038387;	}
.compose-form .compose-form__warning a { color:#111;	}

/* @s and #s */
.status-link.mention {	color: #038387; }

/* toot button */
button.button { 
	border-radius: 0;
	background-color: #038387;	}
button.button:hover { 
	background-color:rgba(255,255,255,15%);
	transition: .2s ease-out;	}

/* killing rounded corners */
.dropdown-menu { border-radius:0;	}
.account__avatar { border-radius: 0;	}

.compose-form .spoiler-input__input { border-radius:0;	}
.compose-form .autosuggest-textarea__textarea { border-radius: 0;	}
.compose-form .compose-form__buttons-wrapper { border-radius:0;	}

.emoji-picker-dropdown__modifiers__menu { border-radius:0; }

/* emoji changes */
.emoji-picker-dropdown__menu { 
	border-radius:0;
	left: 335px !important;
	top: 334px !important;	}

.emoji-button {
	background-image:url('https://chaos.social/emoji/1f608.svg');
	background-repeat: no-repeat;
    background-position: center;
    filter: grayscale(100%);
    opacity: 0.8;
    transition: .2s ease;	}

.emoji-button:active, .emoji-button:focus { filter: none; opacity: 1; transition: .2s ease;	}
.emoji-button img { opacity:0 !important;	}

/* switches */
.react-toggle--checked .react-toggle-track { background-color:#038387;	}
.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track { background-color:#037378;	}

/* picture at the bottom of the drawer */
.drawer__inner__mastodon { display:none;	}

/* links from sidebar (excl. home & notif) */
.drawer__tab { color:#fff;	}
.drawer__header { 
	background: rgba(255,255,255,5%);
	display:inline-block; width:50px;
	position:fixed; left:10px; bottom:0;	}

.drawer__header a:hover { background-color: inherit; color:#038387; transition: .2s ease-out;	}

/* home & notif bars styled to match the other ones */
.tabs-bar { 
	background: rgba(255,255,255,5%);
	display:inline-block;
	position:fixed;
	margin-left:0; z-index:1;
	bottom: 250px; left:10px;
	height:96px; width:50px;
	overflow:hidden;	}
    
.tabs-bar__link { border: none; color: #fff;	}

.tabs-bar__link.active { border:none; color: #fff; background-color: transparent;	}

.tabs-bar__link:hover { background-color: inherit; color:#038387; transition: .2s ease-out;	}
    
.tabs-bar span { display:none;	}

/* main section top bar*/
.column-header { background:rgba(255,255,255,10%); border-top:2px solid #038387;	}
.column-header__button { background:rgba(255,255,255,0%);	}
.column-header__collapsible-inner { background:rgba(255,255,255,6%);	}


/* the main background for the page */
.columns-area { background:#111;	}

/* profile pages */
.icon-button.active { color:#038387;	}

.account__header .account__header__username { color: #038387;	}
.column-back-button { background:rgba(255,255,255,10%); border-top:2px solid #038387;	}
.account--follows-info, .account--muting-info { border-radius:0;	}

.account__header > div { background: rgba(0,0,0,80%);	}

.account__disclaimer { background-color: #038387; color: #111;	}
.account__action-bar { background-color:#111;	}

.account__action-bar-dropdown { background-color: rgba(255,255,255,10%);	}
.account__action-bar-links  { background-color: rgba(255,255,255,10%);	}

.account__section-headline { background-color: rgba(255,255,255,10%);	}
.account__section-headline a { background-color:#111;	}

.account__section-headline a.active { border-bottom:2px solid #038387;	}

.account__header__avatar { border-radius:0 !important; border: 2px solid #038387; transition:0.2s ease;	}
.account__header__avatar:hover { opacity: 0.7; transition:0.2s ease;	}

/* hiding ugly ass triangles */
.account__section-headline a.active::after, .account__section-headline a.active::before { display:none;	}

/* force single column */
.column { display:none;	}
.column:last-child { display:flex;	min-width:50%; max-width:65%;	}

/* smaller back button, no text */
.column-header__back-button { background-color:inherit; color:#038387; font-size:.8em;	}
.column-header__back-button span { display:none;	}
.column-back-button span { display:none;	}
.column-back-button__icon { color:#038387; font-size:.8em;	}

/* hide search cuz i dont use it and dont feel like styling it */
.search { display:none;	}
}
