css:
@-moz-document url-prefix("https://google.github.io/styleguide") {
body {
	background-color: #333 !important;
	color: #eee;
}

/* Links */
a:link {
	color: #66f;
}

a:visited {
	color: #8d40c7;
}

a:hover {
	color: #1c52ff;
	border: 1px dotted #1c52ff;
	margin-right: -1px;
	margin-left: -1px;
}

a:active {
	color: #1c52ff;
	border: 1px solid #1c52ff;
}

.link_button {
	background-color: #333;
	border-color: #66f;
}

/* Tips */
.tip {
	background-color: rgb(150, 145, 105);
}


/* Notes */
.note {
	background-color: rgb(144, 160, 188);

}


/* Table Header */
th {
	background-color: rgb(144, 160, 188);
	border-left: 1px solid #fff;
	border-right: 1px solid #fff;
}


/* Code Samples */
/* General */
code, pre, var {
	background-color: #141F2E !important;
	color: #f5f5f5;
	border: 1px solid black;
	font-family: "Meslo LG S", monospace !important;
	padding: 0px;
}

/* Normal/Plain and Punctuation*/
.pln, .pun {
	color: #f5f5f5;
}

/* Strings and Attribute Values in "" */
.str, .atv  {
	color: #11cf35;
}

/* Tags and Types */
.tag, .typ {
	color: #91ccff;
}

/* Attribute/Attention */
.atn {
	color: #b010b0;
}

/* Declaration */
.dec {
	color: #f5f5f5;
	font-weight: bold;
}

/* Comments */
.com {
	color: #aaa;
}

/* Literals (numeric values) */
.lit {
	color: #ffaa00;
}

/* Keywords */
.kwd {
	color: #558eff;
}

/* Incorrect code examples */
.badcode, code.badcode {
	color: #f5f5f5;
	border-bottom: 3px solid #d2803e;
}

}
