css:
@-moz-document url-prefix(https://github.com) {
  html, body {
    color: #eee;
    background-color: #1e1e1e;
  }

  .container {
    min-width: 980px;
  }

  .dashboard .news {
    background: #234;
    border: 1px solid #ccc;
    border-radius: 0.3em;
    padding-top: 0.6em;
    padding-bottom: 1em;
  }

  .warning {
    background-color: #fffccc;
    color: #900;
  }

  .warning table.files td.icon {
    color: #900;
  }

  .header, .site-header {
    background-color: #234;
    border-bottom: 1px solid #E5E5E5;
    color: #ddd;
  }

  .site-footer {
    color: #ccc;
  }

  .protip {
    margin-top: 20px;
    margin-bottom: 20px;
    text-align: center;
    border: 1px solid #5b5d63;
    background: #123;
    border-radius: 4px;
    padding: 0.5em 1em;
  }

  .repohead h1 {
    color: #eee;
  }

  .header-logo-wordmark {
    color: #fff;
  }

  .header-logo-wordmark:hover {
    color: #7bf;
  }

  .header-logo-invertocat {
    color: #fff;
  }

  .header-logo-invertocat:hover {
    color: #7bf;
  }

  .header-nav-link {
    color: #fff;
  }

  .header-nav-link:hover {
    color: #7bf;
  }

  .auth-form-header {
    color: #FFF;
    text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.3);
    background-color: #123;
    border: 1px solid #789;
    border-radius: 5px 5px 0px 0px;
  }

  .auth-form-body {
    background-color: #234;
    border-width: 0px 1px 1px;
    border-style: none solid solid;
    border-color: -moz-use-text-color #789 #789;
    border-image: none;
    border-radius: 0px 0px 5px 5px;
  }

  .session-authentication {
    background-color: #456;
  }

  .session-authentication .auth-form-header {
    background-color: transparent;
    color: #eee;
  }

  .session-authentication .header-logo {
    color: #fff;
  }

  .header-search-wrapper {
    padding: 0;
  }

  .url-field {
    min-height: 26px;
  }

  /** Unread notifications indicator **/

  .notification-indicator .mail-status {
    background-image: linear-gradient(#9cf674, #7bf);
    color: #fff;
    border: 2px solid #E5E5E5;
  }

  .main-content {
    padding-bottom: 24px;
  }

  .discussion-timeline-actions {
    border-top: 2px solid transparent;
    background: transparent;
  }

  .thread-subscription-status {
    color: #fff;
    background: transparent;
    border: 1px solid #E5E5E5;
  }

  .progress-bar {
    background-color: #678;
  }

  .sunken-menu {
    background-image: linear-gradient(to right, #234 0%, #456 8px);
    box-shadow: 1px 0px 0px #456 inset;
  }

  .sunken-menu::before {
    background-color: transparent;
    background-image: linear-gradient(#456, rgba(255, 255, 255, 0));
  }

  .sunken-menu::after {
    background-color: transparent;
    background-image: linear-gradient(rgba(255, 255, 255, 0), #456);
  }

  .sunken-menu-item .octicon {
    color: #ccc;
  }

  .sunken-menu-item:focus .octicon, .sunken-menu-item:hover .octicon {
    color: #fff;
  }

  .sunken-menu-item.selected {
    color: #fff;
    background-color: #456;
    border-color: #678 #0c0 #678 transparent;
    border-radius: 0px 4px 4px 0px;
    box-shadow: 3px 1px 3px rgba(0, 0, 0, 0.35);
  }

  .sunken-menu-item.selected .octicon {
    color: #fff;
  }

  .sunken-menu-item.selected:after {
    width: 3px;
    background-color: #0c0;
    border-radius: 0 3px 3px 0;
  }

  .sunken-menu-separator {
    background-image: radial-gradient(farthest-side at left top, #345, rgba(244, 244, 244, 0));
  }

  .sunken-menu-separator::before {
    background-image: linear-gradient(to right, #678 70%, #89a 100%);
  }

  .tabnav-pr .tabnav-tab, .tabnav-tab {
    color: #ddd;
    background-color: #345;
    border: 1px solid #789;
    border-bottom: none;
    margin-bottom: 1px;
    border-radius: 3px 3px 0 0;
  }

  .tabnav-tab:hover, .tabnav-tab:focus {
    color: #eee;
  }

  .tabnav-tab.selected {
    background: #789;
    color: #eee;
  }

  .tabnav-pr .tabnav-tab.selected {
    color: #fff;
  }

  .tabnav-extra {
    color: #ddd;
  }

  a.tabnav-extra:hover {
    color: #7bf;
  }

  .subnav-item {
    border: 1px solid #789;
    background-color: #345;
    color: #ccc;
  }

  .subnav-item.selected, .subnav-item.selected:hover, .subnav-item.selected:focus {
    color: #eee;
    background: #337ab7;
    border-color: #0366d6;
  }

  .subnav-item:hover, .subnav-item:focus {
    background-color: #1f5d92;
    color: #eee;
  }

  .subnav-search-context .btn {
    color: #fff;
  }

  .text-emphasized {
    color: #fff;
  }

  .text-muted, .text-gray {
    color: #ccc !important;
  }

  .repository-meta {
    color: #eee;
  }

  .repo-list-item {
    background: #456;
    border: 1px solid #ccc;
    border-radius: 0.4em;
    padding: 1em 1em 4em 1em;
    margin: 1em -1em;
  }

  .repo-list-item .repo-list-stats, .repo-list-item .repo-list-name, .repo-list-item .repo-list-description, .repo-list-item .repo-list-meta {
    z-index: 2;
    position: relative;
  }

  .repo-list .participation-graph {
    left: 1em;
    z-index: 1;
    color: #9cf674;
    opacity: 0.2 !important;
  }

  .repo-list .participation-graph .bars rect {
    fill: #9cf674 !important;
  }

  .private .mini-repo-list-item {
    background-color: #345;
  }

  .mini-repo-list-item .repo-description, .mini-repo-list-item .repo-icon, .mini-repo-list-item .stars {
    color: #eee;
    fill: currentColor;
  }

  .filter-bar {
    background-color: #345;
    border-bottom: 1px solid #E5E5E5;
  }

  .repo-filterer .repo-filter.filter-selected {
    color: #fff;
    border-bottom-color: #7bf;
  }

  .feature-callout {
    color: #666;
    background-color: #123;
    border: 1px solid #e9e9e9;
    border-top: 1px dashed #ddd;
    border-bottom: 0;
  }

  .feature-callout .feature-dismiss-link {
    color: #7bf;
  }

  /**************************************************/

  /* Home Page */

  /**************************************************/

  .bootcamp h1 {
    background-color: #234;
    border-bottom: none;
  }

  .bootcamp .bootcamp-body {
    background-color: #345;
    /* border-color: #345 #678 #678 #678; */
    border-color: #678;
  }

  .bootcamp ul li {
    background-image: linear-gradient(#fff, #999);
    border: 1px solid #678;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.3);
  }

  input.input-contrast, .input-contrast {
    background-color: #234;
    color: #fff;
    border-color: #678;
  }

  input.input-contrast:focus, .input-contrast:focus {
    background-color: #345;
    color: #fff;
  }

  .site-header-nav-main .nav-item {
    border-top: 0;
    border-bottom: 4px solid transparent;
  }

  .site-header-nav .nav-item {
    color: #ccc;
  }

  .site-header-nav .nav-item:hover {
    color: #fff;
  }

  .pagehead {
    border-bottom: 1px solid #46484f;
  }

  .pagehead-nav-item {
    color: #ccc;
  }

  .pagehead-nav-item:hover {
    color: #fff;
  }

  .pagehead-nav-item.selected {
    color: #fff;
    border-bottom: 3px solid #7bf;
  }

  /* Explore GitHub */

  .collection-card {
    background: #345;
    border: 1px solid #678;
    border-radius: 3px;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.3);
  }

  .collection-card-meta {
    color: #ccc;
  }

  /**************************************************/

  /* STATES */

  /**************************************************/

  .state {
    background-color: #999;
  }

  .state-open, .state-proposed, .state-reopened {
    background-color: #7bf;
    color: #fff;
  }

  .state-closed {
    background-color: #DD2C00;
  }

  .state-merged {
    background-color: #6E5494;
    border: 1px solid #9e84c4;
  }

  .ajax-pagination-form .ajax-pagination-btn {
    background: #0366d6;
    color: #eee;
  }

  .ajax-pagination-form .ajax-pagination-btn:hover, .ajax-pagination-form .ajax-pagination-btn:focus {
    background: #3C9614;
  }

  .orghead {
    background-color: #123;
    color: #ccc;
    border-bottom: 1px solid #5b5c62;
  }

  .org-link {
    color: #ccc;
  }

  .org-link:hover {
    color: #7bf;
  }

  .member-username {
    color: #eee;
  }

  .member-list-item .member-link:hover .member-username {
    color: #7bf;
  }

  .member-info .member-fullname {
    color: #ccc;
  }

  .team-grid .team {
    background: #234;
  }

  .team-grid .team-name {
    color: #fff;
  }

  .team-grid .team-members {
    background-color: #456;
  }

  .feature .intro {
    color: #ccc;
  }

  .full-width-divider {
    background: #456;
    border-top: 1px solid #ddd;
  }

  .sub-nav > h2, .sub-nav > h2 > a {
    color: #ccc;
  }

  .sub-nav li .active {
    color: #fff;
    border-bottom: 2px solid #d8d8d8;
  }

  .sub-nav li a {
    color: #ccc;
  }

  .sub-nav li a:hover {
    color: #7bf;
  }

  .sidebar-module h3 a {
    color: #eee;
  }

  .sidebar-module h3 a:hover {
    color: #7bf;
    background-color: #345;
  }

  .sidebar-module ul ul li {
    border-bottom: 1px solid #789;
    color: #666;
    background-color: #345;
  }

  .sidebar-module ul ul li a:hover {
    background-color: #234;
    color: #7bf;
  }

  .sidebar-module.notice {
    background: #234;
    border: 1px solid #789;
  }

  .api-status a {
    color: #7bf;
  }

  .api-status a:hover {
    color: #7bf;
  }

  .api-status .good {
    color: #7bf;
  }

  .content {
    color: #ccc;
  }

  .content .sectionbody .dlist dt, .content .verseblock-content, .content dl code, .content p > tt, .content ul code, .content p code {
    border: 1px solid #789;
    background-color: #234;
  }

  .content table tr {
    border-top: 1px solid #789;
    background-color: #234;
  }

  #markdown-toc li a {
    color: #eee;
    text-decoration: underline;
  }

  #markdown-toc li a:hover {
    color: #7bf;
  }

  /**************************************************/

  /*************** BUTTONS AND LINKS ****************/

  /**************************************************/

  .branch-name {
    background-color: #234;
    border: 1px solid #789;
    color: #fff;
  }

  a {
    color: #7bf;
  }

  a.branch-name {
    color: #fff;
  }

  a.text-gray-light, a.link-gray-dark {
    color: #7bf !important;
  }

  .link-gray-dark:hover {
    color: #7bf !important;
  }

  .text-gray-light {
    color: #dfdfdf !important;
  }

  .btn {
    color: #fff;
    background: #3c4c69;
    border: 1px solid #626262;
    /* box-shadow: 0 0 2px rgba(0, 0, 0, 0.7); */
  }

  .btn:disabled, .btn:disabled:hover, .btn.disabled, .btn.disabled:hover {
    background: #659dce;
    color: #eee;
  }

  .btn:hover, .btn:active, .btn.selected, .btn.zeroclipboard-is-hover, .btn.zeroclipboard-is-active {
    background: #345;
    border-color: #678;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.15) inset;
  }

  .btn-primary {
    color: #fff;
    text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.15);
    background: #337ab7;
    border-color: #337ab7;
  }

  .btn-primary:hover {
    color: #eee;
    background: #2166a2;
  }

  .btn-primary:disabled, .btn-primary.disabled {
    color: #eee;
    background: #659dce;
  }

  .btn-link {
    color: #fff;
    background-color: transparent;
    border: 0px none;
    -moz-user-select: none;
  }

  .review-simple-reply-button:hover, .review-simple-reply-button:active {
    color: #eee;
    background: #2166a2;
  }

  .review-simple-reply-button {
    color: #eee;
    background: #2166a2;
  }

  .muted-link {
    color: #fff !important;
  }

  .muted-link:hover {
    color: #7bf !important;
    text-decoration: underline !important;
  }

  .collection-tools .select-menu-button {
    color: #ccc;
  }

  .collection-tools .select-menu-button:hover {
    color: #7bf;
  }

  .button, .button-secondary {
    background-color: #7bf;
    color: #fff;
  }

  .button:hover, .button-secondary:hover {
    background-color: #9cf674;
  }

  /**************************************************/

  /* Lighten fonts */

  /**************************************************/

  .text-danger {
    color: #FD6C40;
  }

  .text-error, .text-failure {
    color: #FF3C00;
  }

  .blankslate {
    background-color: #234;
    border: 1px solid #E5E5E5;
    border-radius: 3px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.05) inset;
  }

  a.member-row {
    color: #7bf;
  }

  a.member-row:hover {
    color: #7bf;
  }

  .radio-label {
    color: #ccc;
  }

  .news .alert .octicon {
    color: #eee;
  }

  .news .alert .mega-octicon {
    color: #eee;
  }

  .news .alert .simple .title {
    color: #ddd;
  }

  .lead {
    color: #ddd;
  }

  .setup-header {
    text-shadow: 0px 1px 2px rgba(0, 0, 0, 0.5);
  }

  .setup-header .lead a {
    color: #fff;
  }

  .setup-header .lead a:hover {
    color: #7bf;
  }

  .gh-header-title {
    color: #fff;
  }

  .gh-header-number {
    color: #bbb;
  }

  .gh-header-meta {
    color: #ddd;
  }

  .gh-header-meta .author {
    color: #fff;
  }

  .filter-item {
    color: #ddd;
  }

  .filter-item:hover {
    color: #ddd;
    background-color: #3C9614;
  }

  .filter-item.selected {
    color: #fff;
    background-color: #337ab7;
  }

  .subhead-description {
    color: #ccc;
  }

  .note {
    color: #bcd;
  }

  .form-checkbox .note {
    color: #abc;
  }

  .reponame-suggestion {
    color: #94c86a;
    cursor: pointer;
  }

  .owner-reponame .slash {
    color: #bbb;
  }

  p.explain {
    color: #ccc;
  }

  .org-header-meta .meta-item .meta-link {
    color: #fff;
  }

  .org-header-meta .meta-item .meta-link:hover {
    color: #7bf;
  }

  .org-module-link {
    color: #fff;
  }

  .org-stats {
    color: #fff;
  }

  .org-module-link:hover, .org-module-link:hover .org-stats {
    color: #7bf;
  }

  .vcard-username {
    color: #ddd;
  }

  .vcard-detail .octicon {
    color: #eee;
  }

  .contrib-number {
    color: #cde;
  }

  .table-list {
    color: #ccc;
    background: #2d2d2d;
  }

  .table-list-bordered {
    border-bottom-color: #5b5d63;
  }

  .table-list-bordered .table-list-cell:first-child {
    border-left: 1px solid #5b5d63;
  }

  .table-list-bordered .table-list-cell:last-child {
    border-right: 1px solid #5b5d63;
  }

  .table-list-cell {
    border-top: 1px solid #5b5d63;
  }

  .commits-list-item+.commits-list-item {
    border-top: 1px solid #5b5d63;
  }

  .table-list-milestones .stat-label {
    color: #ddd;
  }

  .table-list-milestones .stat {
    color: #cde;
  }

  .milestone-title-link a {
    color: #fff;
  }

  .milestone-title-link a:hover {
    color: #7bf;
  }

  .subscriptions-content .sorted-by {
    color: #eee;
  }

  .subscriptions-content .repo-icon {
    color: #eee;
  }

  .label-description {
    color: #ddd;
  }

  .labels-list-action {
    color: #eee;
  }

  .labels-list-action:hover {
    color: #7bf;
  }

  .discussion-item .author {
    color: #fff;
  }

  .discussion-item-header {
    color: #ddd;
  }

  .discussion-sidebar-heading {
    color: #ddd;
  }

  .discussion-sidebar-toggle, .discussion-sidebar-toggle .octicon {
    color: #fff;
  }

  .discussion-sidebar-toggle:hover {
    color: #7bf;
  }

  .discussion-sidebar-item {
    color: #ddd;
  }

  .discussion-item-ref-title .title-link {
    color: #ddd;
    text-decoration: underline;
  }

  .discussion-item-ref-title .title-link:hover {
    color: #7bf;
    text-decoration: underline;
  }

  .discussion-item-ref-title .title-link:hover .issue-num {
    color: inherit;
  }

  .discussion-item-ref-title .issue-num {
    color: #ccc;
  }

  .discussion-item-entity {
    color: #7bf;
  }

  .discussion-item-entity:hover {
    color: #7bf;
  }

  .discussion-item .renamed-was, .discussion-item .renamed-is {
    color: #eee;
  }

  .discussion-item-icon {
    background-color: #123;
    border: 2px solid #fff;
    color: #fff;
    line-height: 26px;
  }

  .repo-list-description {
    color: #ddd;
  }

  .repo-list-meta {
    color: #eee;
  }

  .repo-list-stats {
    color: #ddd;
  }

  .markdown-body .highlight pre, .markdown-body pre {
    background-color: #234;
    border-radius: 3px;
    border: 1px solid #789;
  }

  .markdown-body blockquote {
    color: #dfd;
    border-left: 4px solid #6c6;
  }

  .markdown-body code, .markdown-body tt {
    color: #ddd;
    background-color: #59626e;
    padding: 2px 3px;
  }

  .markdown-body table tr {
    background-color: #234;
    border-top: 1px solid #789;
  }

  .markdown-body table tr:nth-child(2n) {
    background-color: #345;
  }

  .markdown-body h1 .octicon-link, .markdown-body h2 .octicon-link, .markdown-body h3 .octicon-link, .markdown-body h4 .octicon-link, .markdown-body h5 .octicon-link, .markdown-body h6 .octicon-link {
    color: #ff0;
    vertical-align: middle;
    visibility: hidden;
  }

  .markdown-body li {
    color: #ddd;
  }

  .sidebar-assignee .assignee {
    color: #fff;
  }

  .sidebar-assignee .assignee:hover {
    color: #7bf;
  }

  .milestone-name {
    color: #fff;
  }

  .milestone-name:hover {
    color: #7bf;
  }

  .lock-toggle-link {
    color: #fff;
  }

  .lock-toggle-link:hover {
    color: #7bf;
  }

  .news blockquote {
    color: #ddd;
  }

  .news div.message, .news li blockquote {
    color: #eee;
  }

  .protip-callout {
    color: #eef;
  }

  .protip code {
    background-color: #234;
    padding: 2px 8px;
    border: 1px solid #678;
  }

  .subscribe-feed {
    color: #ddd;
  }

  .octofication .message {
    background-color: #234;
    border: 1px solid #7bf;
  }

  .octofication .message p {
    color: #ccc;
  }

  .octofication .broadcast-icon {
    color: #7bf;
  }

  .user-nav .dropdown-item {
    color: #eee;
  }

  /**************************************************/

  /* CONTAINERS */

  /**************************************************/

  .org-header {
    color: #ccc;
    border-bottom: 1px solid #EEE;
    background-color: #234;
  }

  .org-name {
    color: #fff;
  }

  .pagehead-tabs-item {
    color: #ccc;
    border: 1px solid #5b5c62;
    border-bottom: transparent;
    margin: 0 5px 0 0;
  }

  .pagehead-tabs-item:hover {
    color: #fff;
  }

  .pagehead-tabs-item.selected {
    color: #fff;
    background-color: #456;
    border-top: 2px solid #7bf;
    border-color: #7bf #46484f transparent;
  }

  .pagehead-tabs-item.selected:hover {
    color: #345;
  }

  .profilecols .filter-bar {
    background: none;
    border-bottom: 1px solid #DDD;
  }

  .menu-heading {
    color: #eee;
    background-color: #234;
    border-bottom: 1px solid #789;
  }

  .simple-conversation-list, .authors-and-code {
    background: #234;
    padding: 0.4em;
    border-radius: 4px;
    border: 1px solid #ccc;
  }

  .diffstat-summary, .diffstat-summary strong {
    color: #ccc;
  }

  .boxed-group > h3, .boxed-group .heading {
    background-color: #234;
    border: 1px solid #46484f;
  }

  .boxed-group-inner {
    background: #345;
    border: 1px solid #ccc;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
    color: #bbb;
  }

  .boxed-group-inner .help {
    border-top: 1px solid #DDD;
