css:
@-moz-document url("http://192.168.0.1/index.html"), domain("192.168.0.1") {
body {
    background: #2f2f2f !important;
}
.boxed_content {
    background: #1b1b1b;
}
.button_containers {
    background: #1b1b1b;
}
.button_containers {
    color: #c5c5c5;
}
.level1 {
    color: #b9b9b9 !important;
}
.section_title {
    color: #c5c5c5 !important;
}
.section_title {
    color: #c5c5c5 !important;
}
.boxed_content div.section_title {
    background: #3c3c3c!important;
}
.page-title {
    display: none;
}
.language {
    right: 0px;
    font-size: 16px;
    color: rgb(225, 225, 225);
    margin: 0px;
    display: none;
}
.select {
    display: inline-block;
    position: relative;
    border: 2px solid #9e5050a3 !important;
    border-radius: 3px;
}
.logout {
    right: 230px;
    padding: none;
    border-right: none;
}
.green,
.svg-inject-MTA.green path,
.svg-inject-MTA.green circle,
.diag-svg-inject-MTA.green path,
.diag-svg-inject-MTA.green circle,
.PARCON-svg-inject-MTA.green path,
.PARCON-svg-inject-MTA.green circle,
.green,
.svg-inject-CM.green path,
.svg-inject-CM.green circle,
.diag-svg-inject-CM.green path,
.diag-svg-inject-CM.green circle,
.PARCON-svg-inject-CM.green path,
.PARCON-svg-inject-CM.green circle {
    color: #69fffc!important;
    fill: #69fffc!important;
}
.green,
.svg-inject-P2.green path,
.svg-inject-P2.green circle,
.diag-svg-inject-P2.green path,
.diag-svg-inject-P2.green circle,
.PARCON-svg-inject-P2.green path,
.PARCON-svg-inject-P2.green circle {
    color: #69fffc;
    fill: #69fffc!important;
}
.section_header {
    color: #d8d8d8;
}
.general_text {
    color: #ffffff;
}
.data-tables th {
    background: #dedede;
    padding: 14px 18px;
    font-size: 14px;
    color: #42908e;
    font-weight: normal;
    border: 1px solid #ffffff;
    border-width: 0px 1px 0px 1px;
    text-align: left;
}
.data-tables {
    border: 1px solid #42908e;
}
.button_main {
    background: #42908e;
    border: 1px solid #42a4bc;
}
.data-tables td {
    background: #3a3a3a;
    padding: 14px 18px;
    font-size: 14px;
    color: #bfbfbf;
    border: 1px solid #42908e;
}
.select select {
    background: none repeat scroll 0% 0% #292929;
    color: #b9b9ac;
}
.select {
    border: 2px solid #387d7aa3 !important;
}
select.disabled {
    background-color: #292929 !important;
}
.selectArrowDisable:before {
    background-color: #1b1b1b;
}
.level1 span:hover {
    color: #42908e;
}
.level3 a:hover {
    color: #42908e;
}
.levelmenu a:hover {
    color: #42908e;
}
.select_title {
    color: #c3c3c3 !important;
}
.select:before {
    background-color: #1b1b1b;
}
.button_secondary_arrowfree_deactivated {
    display: none;
    /*AP button functionality doesn't actually work for me so it was removed, change if you need it.'*/
}
input.disabled,
textarea.disabled {
    border: 2px solid #42908e;
    background: #292929;
}
.select {
    border: 2px solid #42908e !important;
}
input.default {
    background: #292929;
    color: #c3c3c3;
    border: 2px solid #42908e;
}
.select select {
    color: #c3c3c3;
}
.disabled,
.svg-inject.disabled path,
.svg-inject.disabled {
    color: #c3c3c3 !important;
}
}

@-moz-document url("http://192.168.0.1/common_page/login.html") {
.overlay {
    display: none;
}
.button_main_deactivated {
    color: #ffffff;
    background: #925959;
}
}
